{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Whether intratumoral delivery of an mRNA vaccine can create an interferon-rich inflammatory state that improves sensitivity to anti-PD-L1 therapy.",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy",
      "label": "Mouse efficacy cohort (combination therapy)",
      "sample_size_or_observations": "78 tumor-bearing mice",
      "description": "Preclinical pooled mouse experiments testing intratumoral mRNA vaccine combined with anti-PD-L1 therapy for tumor control."
    },
    {
      "id": "mouse-controls",
      "label": "Mouse control cohort (single agents and vehicle)",
      "sample_size_or_observations": "84 tumor-bearing mice",
      "description": "Matched pooled mouse controls including vaccine only, ICI only, and vehicle, used to assess null and conditional effects."
    },
    {
      "id": "human-retrospective",
      "label": "Human retrospective metastatic cohort on ICI",
      "sample_size_or_observations": "130 patients (43 vaccinated; 87 unvaccinated)",
      "description": "Retrospective metastatic patient cohort receiving immune checkpoint inhibitor treatment, compared by prior SARS-CoV-2 mRNA vaccination status."
    }
  ],
  "major_limitations": [
    "Human analysis is retrospective and non-randomized; residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors.",
    "Some mechanistic readouts were strongest in specific model systems rather than uniformly across all tested settings."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
