            "term": "Immune Checkpoint Inhibitors (ICIs)",
            "definition": "A type of cancer immunotherapy that blocks proteins called checkpoints, which can prevent the immune system from attacking cancer cells."
        },
        {
            "term": "mRNA Vaccine",
            "definition": "A type of vaccine that uses a molecule called messenger RNA (mRNA) to teach the body's cells how to make a protein that triggers an immune response."
        },
        {
            "term": "Intratumoral",
            "definition": "Within a tumor."
        },
        {
            "term": "Type I Interferon",
            "definition": "A group of proteins that play a key role in the body's innate immune response to viral infections and cancer."
        },
        {
            "term": "Retrospective Study",
            "definition": "A study that looks back in time at data that has already been collected."
        }
