{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Whether intratumoral mRNA vaccination can induce an interferon-rich tumor state that improves response to anti-PD-L1 immune checkpoint blockade.",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy-cohort",
      "label": "Mouse efficacy cohort (combination therapy)",
      "sample_size_or_observations": "78 tumor-bearing mice",
      "description": "Pooled repeat experiments testing combined intratumoral mRNA vaccine plus anti-PD-L1 treatment in murine tumor models."
    },
    {
      "id": "mouse-control-cohort",
      "label": "Mouse control cohort",
      "sample_size_or_observations": "84 tumor-bearing mice",
      "description": "Matched controls included vaccine-only, ICI-only, and vehicle conditions to benchmark whether combination effects exceeded single-agent activity."
    },
    {
      "id": "human-retrospective-metastatic",
      "label": "Human retrospective metastatic cohort",
      "sample_size_or_observations": "130 patients total",
      "description": "Retrospective analysis of metastatic patients receiving ICI, stratified by prior SARS-CoV-2 mRNA vaccination status."
    },
    {
      "id": "human-vaccinated-subgroup",
      "label": "Human vaccinated subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subset of the retrospective metastatic cohort with prior mRNA vaccination before or around ICI treatment."
    },
    {
      "id": "human-unvaccinated-subgroup",
      "label": "Human unvaccinated subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Subset of the retrospective metastatic cohort without prior SARS-CoV-2 mRNA vaccination for comparison of survival outcomes."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized, so residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors.",
    "Some mechanistic readouts were strongest in specific model systems rather than uniformly across settings."
  ]
}

