{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Does intratumoral SARS-CoV-2 mRNA vaccination prime tumors to respond more effectively to anti-PD-L1 checkpoint blockade?",
  "datasets_or_cohorts": [
    {
      "id": "mouse-combo",
      "label": "Mouse combination therapy cohort",
      "sample_size_or_observations": "78 tumor-bearing mice pooled across repeat experiments",
      "description": "Preclinical cohort testing intratumoral mRNA vaccine plus anti-PD-L1 therapy for tumor control in multiple murine models."
    },
    {
      "id": "mouse-control",
      "label": "Mouse matched control cohorts",
      "sample_size_or_observations": "84 tumor-bearing mice across vaccine-only, ICI-only, and vehicle groups",
      "description": "Matched control tumors evaluating vaccine-only, ICI-only, and vehicle treatments without combined therapy."
    },
    {
      "id": "human-retrospective",
      "label": "Retrospective metastatic patient cohort",
      "sample_size_or_observations": "130 metastatic patients total",
      "description": "Retrospective analysis comparing ICI outcomes by prior SARS-CoV-2 mRNA vaccination status among metastatic patients."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective, non-randomized, and likely confounded.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Fraction of tumor proteins represented in the immunopeptidome",
      "value": "62.3%",
      "comparator_or_baseline": "37.3%",
      "interpretation": "Broader antigen display after intratumoral mRNA vaccination",
      "evidence_line_refs": [
        "L28"
      ]
    },
    {
      "id": "Q02",
      "metric": "Fraction of proteins represented in the MHC-I peptidome",
      "value": "40.6%",
      "comparator_or_baseline": "20.6%",
      "interpretation": "Strong expansion of the MHC-I-presented landscape",
      "evidence_line_refs": [
        "L29"
      ]
    },
    {
      "id": "Q03",
      "metric": "Retrospective human survival comparison p-value",
      "value": "p=0.01",
      "comparator_or_baseline": "N/A",
      "interpretation": "Vaccinated subgroup had improved survival under ICI treatment",
      "evidence_line_refs": [
        "L30"
      ]
    },
    {
      "id": "Q04",
      "metric": "Type I interferon response score (normalized units)",
      "value": "3.1",
      "comparator_or_baseline": "1.0",
      "interpretation": "Marked induction after mRNA vaccination in tumors",
      "evidence_line_refs": [
        "L31"
      ]
    },
    {
      "id": "Q05",
      "metric": "PD-L1 expression fold-change in tumor tissue",
      "value": "2.4x",
      "comparator_or_baseline": "1.0x",
      "interpretation": "Checkpoint axis became more targetable",
      "evidence_line_refs": [
        "L32"
      ]
    },
    {
      "id": "Q06",
      "metric": "Intratumoral CD8+ T-cell density fold-change",
      "value": "1.8x",
      "comparator_or_baseline": "1.0x",
      "interpretation": "Increased immune infiltration with combination therapy",
      "evidence_line_refs": [
        "L33"
      ]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "Vaccine-only treatment did not produce durable tumor control in non-immunogenic models.",
      "evidence_line_ref": "L36"
    },
    {
      "statement": "ICI-only treatment had limited activity in low-immunogenic baseline settings.",
      "evidence_line_ref": "L37"
    },
    {
      "statement": "Lipid particle controls lacking mRNA did not reproduce the sensitization effect.",
      "evidence_line_ref": "L38"
    }
  ]
}

{
  "claims": [
    {
      "claim_id": "C01",
      "claim": "Intratumoral SARS-CoV-2 mRNA vaccination sensitizes tumors to anti-PD-L1 checkpoint blockade in murine models.",
      "strength": "strong",
      "evidence": [
        {
          "quote": "Combined intervention improves tumor control in mice and aligns with better outcomes in the retrospective human cohort.",
          "line_ref": "L46"
        },
        {
          "quote": "Intratumoral mRNA vaccination triggers local innate sensing and type I interferon signaling.",
          "line_ref": "L42"
        }
      ],
      "caveat": "Findings in mouse models may not translate to all human tumors."
    },
    {
      "claim_id": "C02",
      "claim": "A broader peptide repertoire appears on MHC-I after vaccination, improving tumor visibility to T cells.",
      "strength": "moderate",
      "evidence": [
        {
          "quote": "A broader peptide repertoire appears on MHC-I, improving tumor visibility to T cells.",
          "line_ref": "L44"
        },
        {
          "quote": "Fraction of tumor proteins represented in immunopeptidome | 62.3% | 37.3% | Broader antigen display after intratumoral mRNA vaccination",
          "line_ref": "L28"
        }
      ],
      "caveat": "Antigen presentation changes are shown in mice and may vary by tumor type."
    },
    {
      "claim_id": "C03",
      "claim": "Retrospective human analysis associates prior mRNA vaccination with better survival under ICI treatment.",
      "strength": "tentative",
      "evidence": [
        {
          "quote": "Retrospective human survival comparison p-value | p=0.01 | N/A | Vaccinated subgroup had improved survival under ICI treatment",
          "line_ref": "L30"
        },
        {
          "quote": "Retrospective cohort of metastatic patients receiving ICI treatment, compared by prior SARS-CoV-2 mRNA vaccination status.",
          "line_ref": "L15"
        }
      ],
      "caveat": "The analysis is retrospective and likely confounded, limiting causal claims."
    },
    {
      "claim_id": "C04",
      "claim": "Blocking type I interferon signaling eliminates the sensitization benefit seen with mRNA vaccination.",
      "strength": "moderate",
      "evidence": [
        {
          "quote": "Blocking type I interferon signaling with anti-IFNAR1 eliminated the observed sensitization benefit.",
          "line_ref": "L39"
        },
        {
          "quote": "Intratumoral mRNA vaccination triggers local innate sensing and type I interferon signaling.",
          "line_ref": "L42"
        }
      ],
      "caveat": "The mechanistic chain is described in specific models and may differ in other settings."
    }
  ]
}

{
  "headline": "Intratumoral mRNA vaccination may boost checkpoint blockade sensitivity in tumors",
  "plain_language_summary": "In preclinical mouse models, intratumoral delivery of a SARS-CoV-2 mRNA vaccine triggers type I interferon signaling, broadens the tumor immunopeptidome, and enhances PD-L1 targetability and CD8+ infiltration, aligning with an association between prior vaccination and better survival in a retrospective metastatic patient cohort. Vaccine-only or ICI-only controls showed limited durable benefit, underscoring that the observed tumor sensitization required the combination approach.",
  "what_is_new": [
    "Intratumoral SARS-CoV-2 mRNA vaccination induces local innate sensing and IFN-driven antigen processing, potentially explaining the broadened peptide repertoire on MHC-I.",
    "Broad antigen presentation and increased PD-L1 expression in mouse tumors create a biological rationale for pairing the vaccine with anti-PD-L1 checkpoint blockade.",
    "A retrospective human cohort links prior SARS-CoV-2 mRNA vaccination with improved survival under ICI, consistent with the mouse combination findings.
"  ],
  "why_caution_is_needed": [
    "The human analysis was retrospective, non-randomized, and likely residual confounding remains, so associations rather than causality are supported.",
    "Vaccine-only treatment did not produce durable tumor control in non-immunogenic models, indicating the benefit depends on the combination regimen.",
    "ICI-only treatment had limited activity in low-immunogenic baselines, so improved outcomes cannot be attributed to ICI alone in these settings.",
    "Mouse model effects may not translate directly to all human tumors due to species and tumor-type differences."
  ],
  "glossary": [
    {
      "term": "mRNA vaccine",
      "definition": "A vaccine that delivers messenger RNA encoding antigenic proteins to cells, prompting antigen presentation and immune priming against those proteins."
    },
    {
      "term": "Immune checkpoint blockade",
      "definition": "Therapies such as anti-PD-L1 antibodies that release inhibitory brakes on T cells, enabling stronger anti-tumor responses."
    },
    {
      "term": "Type I interferon signaling",
      "definition": "A cascade of innate immune signals induced by viral sensing that enhances antigen presentation and antiviral states within tissues."
    },
    {
      "term": "Immunopeptidome",
      "definition": "The collection of peptides displayed on MHC molecules, which dictate immune recognition of tumor cells."
    }
  ],
  "open_questions": [
    "Can prospective randomized trials confirm that intratumoral mRNA vaccination causally increases ICI response rates across diverse human tumors?",
    "Which tumor types or immune microenvironments derive the greatest benefit from the combination of mRNA vaccination and checkpoint blockade?",
    "What is the optimal timing and sequencing of vaccination relative to ICI initiation to maximize durable responses?"
  ]
}
