{
  "paper": {
    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does JWST MIRI phase-curve spectroscopy of TRAPPIST-1 b support an atmospheric interpretation over a strict bare-rock/no-redistribution model?",
  "datasets_or_cohorts": [
    {
      "id": "observed-system",
      "label": "Observed TRAPPIST-1 b phase-curve",
      "sample_size_or_observations": "One rocky exoplanet with phase-resolved JWST MIRI thermal spectra",
      "description": "The study observes TRAPPIST-1 b with orbital phase-resolved thermal spectroscopy to constrain day-night heat transport and composition-sensitive spectral structure."
    },
    {
      "id": "no-atmosphere-null",
      "label": "No-atmosphere null model",
      "sample_size_or_observations": "Same observational dataset modeled with atmosphere-free scenario",
      "description": "A bare-rock, no-atmosphere model with minimal heat redistribution is used as a null comparison against atmospheric hypotheses."
    },
    {
      "id": "co2-rich-scenario",
      "label": "CO2-rich atmospheric retrieval",
      "sample_size_or_observations": "Same phase-curve dataset under CO2-rich retrieval assumptions",
      "description": "An atmospheric retrieval branch with significant CO2-related absorption near 15 µm is tested against the observed phase variation and spectrum."
    },
    {
      "id": "o2-rich-scenario",
      "label": "O2-rich atmospheric retrieval",
      "sample_size_or_observations": "Same phase-curve dataset under O2-rich retrieval assumptions",
      "description": "An oxidized atmospheric branch is examined for consistency with thermal and spectral constraints and is not fully rejected by the data."
    }
  ],
  "major_limitations": [
    "Single-planet study: findings apply to TRAPPIST-1 b and do not establish prevalence across all M-dwarf rocky planets.",
    "Retrieval assumptions (circulation, opacity priors, and cloud/haze treatment) strongly influence inferred posterior values.",
    "Stellar variability and instrument systematics may bias weak spectral features in this dataset.",
    "Night-side constraints are weaker than day-side constraints, reducing confidence in full thermal-map interpretation."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Dayside brightness temperature",
      "value": "342 +/- 22 K",
      "comparator_or_baseline": "Bare-rock expectation from no-redistribution model",
      "interpretation": "The dayside is warm, but this value alone is not decisive evidence against a bare surface interpretation.",
      "evidence_line_refs": ["L27"]
    },
    {
      "id": "Q02",
      "metric": "Heat transport efficiency (epsilon)",
      "value": "0.19 (+0.23 / -0.14)",
      "comparator_or_baseline": "0.0 for perfect no-atmosphere limit",
      "interpretation": "The positive transport estimate favors non-zero heat redistribution beyond an immediate bare-rock reradiation limit.",
      "evidence_line_refs": ["L28"]
    },
    {
      "id": "Q03",
      "metric": "Bond albedo upper bound",
      "value": "<0.08",
      "comparator_or_baseline": "Higher-albedo reflective cases",
      "interpretation": "Thermal-energy balance fits suggest the planet is very dark rather than highly reflective.",
      "evidence_line_refs": ["L29"]
    },
    {
      "id": "Q04",
      "metric": "15 um CO2 spectral band depth estimate",
      "value": "96 ppm",
      "comparator_or_baseline": "Flat-spectrum null",
      "interpretation": "A spectral feature near 15 µm is consistent with atmospheric CO2 absorption.",
      "evidence_line_refs": ["L30"]
    },
    {
      "id": "Q05",
      "metric": "Joint fit likelihood ratio (atmosphere vs no atmosphere)",
      "value": "7.4",
      "comparator_or_baseline": "1.0 baseline ratio",
      "interpretation": "Atmospheric retrieval families explain the combined phase-curve and spectral products better than no-atmosphere models.",
      "evidence_line_refs": ["L31"]
    },
    {
      "id": "Q06",
      "metric": "Preferred pressure scale in CO2-rich retrieval family",
      "value": "0.3-1.0 bar",
      "comparator_or_baseline": "Near-vacuum branch",
      "interpretation": "A moderate-pressure atmosphere remains plausible in the CO2-rich branch.",
      "evidence_line_refs": ["L32"]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "A pure no-atmosphere model cannot simultaneously match both phase amplitude and spectral band behavior.",
      "evidence_line_ref": "L35"
    },
    {
      "statement": "O2-rich atmosphere cases are not fully ruled out by current data.",
      "evidence_line_ref": "L36"
    }
  ]
}

{
  "claims": [
    {
      "claim_id": "C01",
      "claim": "The study uses JWST MIRI phase-curve spectroscopy to test whether TRAPPIST-1 b is airless or has an atmosphere.",
      "strength": "strong",
      "evidence": [
        {
          "quote": "Instrument: JWST MIRI low-resolution spectroscopy over an orbital phase curve.",
          "line_ref": "L14"
        },
        {
          "quote": "Inference workflow: retrieval models comparing atmosphere-free and atmosphere-bearing scenarios.",
          "line_ref": "L16"
        }
      ],
      "caveat": "The report is a structured summary, so full observational details, calibrations, and priors are not provided."
    },
    {
      "claim_id": "C02",
      "claim": "Evidence was gathered from both thermal redistribution behavior and a CO2-sensitive spectral feature near 15 micrometers.",
      "strength": "strong",
      "evidence": [
        {
          "quote": "Thermal phase variation and spectral structure around the 15 um CO2 band.",
          "line_ref": "L15"
        },
        {
          "quote": "15 um CO2 spectral band depth estimate | 96 ppm | Flat-spectrum null | Spectral structure is consistent with atmospheric absorption",
          "line_ref": "L30"
        }
      ],
      "caveat": "The exact signal significance depends on retrieval assumptions and remaining instrumental or model limitations."
    },
    {
      "claim_id": "C03",
      "claim": "A strict no-atmosphere interpretation is disfavored because it cannot fit all observables at once.",
      "strength": "moderate",
      "evidence": [
        {
          "quote": "A pure no-atmosphere model cannot simultaneously match both phase amplitude and spectral band behavior.",
          "line_ref": "L35"
        },
        {
