    },
    {
      "id": "mouse-control-cohort",
      "label": "Mouse control cohort",
      "sample_size_or_observations": "84 mice (pooled across matched controls)",
      "description": "Matched murine controls receiving vaccine-only, ICI-only, and vehicle treatment for comparison against sensitization outcomes."
    },
    {
      "id": "human-retrospective-cohort",
      "label": "Human retrospective cohort",
      "sample_size_or_observations": "130 metastatic patients",
      "description": "Metastatic patients receiving immune checkpoint inhibitors, analyzed retrospectively by prior SARS-CoV-2 mRNA vaccination status."
    },
    {
      "id": "human-vaccinated-subgroup",
      "label": "Human vaccinated subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subset of the retrospective cohort previously vaccinated with a SARS-CoV-2 mRNA vaccine before/around ICI treatment."
    },
    {
      "id": "human-unvaccinated-subgroup",
      "label": "Human unvaccinated subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Comparison group of metastatic patients without documented SARS-CoV-2 mRNA vaccination for survival and clinical outcome contrasts."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized, so residual confounding is likely. ",
    "Vaccination timing relative to ICI initiation varied across patients. ",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation. ",
    "Mouse model effects may not map one-to-one to all human tumors. ",
    "Some mechanistic readouts were strongest in specific model systems rather than uniformly across all settings. "
  ]
}

