{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Can intratumoral mRNA vaccination increase tumor sensitivity to anti-PD-L1 immune checkpoint blockade, and are the findings consistent in both murine models and retrospective human ICI-treated patients?",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy-cohort",
      "label": "Mouse efficacy cohort",
      "sample_size_or_observations": "78 mice (pooled across repeat experiments)",
      "description": "Murine efficacy studies testing intratumoral mRNA vaccination combined with anti-PD-L1 in multiple tumor models."
    },
    {
      "id": "mouse-control-cohort",
      "label": "Mouse control cohort",
      "sample_size_or_observations": "84 mice (pooled across matched controls)",
      "description": "Matched murine controls receiving vaccine-only, ICI-only, and vehicle treatment for comparison against sensitization outcomes."
    },
    {
      "id": "human-retrospective-cohort",
      "label": "Human retrospective cohort",
      "sample_size_or_observations": "130 metastatic patients",
      "description": "Metastatic patients receiving immune checkpoint inhibitors, analyzed retrospectively by prior SARS-CoV-2 mRNA vaccination status."
    },
    {
      "id": "human-vaccinated-subgroup",
      "label": "Human vaccinated subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subset of the retrospective cohort previously vaccinated with a SARS-CoV-2 mRNA vaccine before/around ICI treatment."
    },
    {
      "id": "human-unvaccinated-subgroup",
      "label": "Human unvaccinated subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Comparison group of metastatic patients without documented SARS-CoV-2 mRNA vaccination for survival and clinical outcome contrasts."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized, so residual confounding is likely. ",
    "Vaccination timing relative to ICI initiation varied across patients. ",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation. ",
    "Mouse model effects may not map one-to-one to all human tumors. ",
    "Some mechanistic readouts were strongest in specific model systems rather than uniformly across all settings. "
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Tumor immunopeptidome breadth",
      "value": "62.3% vs 37.3%",
      "comparator_or_baseline": "vaccine/combination arm versus comparator condition",
      "interpretation": "Intratumoral mRNA-vaccinated or combination-treated tumors showed a notably larger fraction of proteins represented in the immunopeptidome, indicating broader antigen display.",
      "evidence_line_refs": ["L28"]
    },
    {
      "id": "Q02",
      "metric": "MHC-I peptide landscape breadth",
      "value": "40.6% vs 20.6%",
      "comparator_or_baseline": "combination or vaccine arm versus comparator",
      "interpretation": "MHC-I-presented proteins roughly doubled, supporting expansion of the antigenic peptide repertoire potentially more visible to CD8+ responses.",
      "evidence_line_refs": ["L29"]
    },
    {
      "id": "Q03",
      "metric": "Retrospective survival association under ICI",
      "value": "p=0.01",
      "comparator_or_baseline": "vaccinated versus unvaccinated subgroup",
      "interpretation": "Vaccinated patients in the metastatic cohort had improved survival compared with unvaccinated patients under ICI treatment, reaching the reported significance threshold.",
      "evidence_line_refs": ["L30", "L21", "L23"]
    },
    {
      "id": "Q04",
      "metric": "Type I interferon response score",
      "value": "3.1 normalized units",
      "comparator_or_baseline": "1.0 baseline normalized units",
      "interpretation": "The reported normalized interferon response increased after intratumoral mRNA vaccination, consistent with innate activation in tumors.",
      "evidence_line_refs": ["L31"]
    },
    {
      "id": "Q05",
      "metric": "PD-L1 expression change",
      "value": "2.4x",
      "comparator_or_baseline": "1.0x baseline",
      "interpretation": "PD-L1 levels increased in tumor tissue, supporting the biologic rationale for combining with checkpoint blockade.",
      "evidence_line_refs": ["L32"]
    },
    {
      "id": "Q06",
      "metric": "Intratumoral CD8+ T-cell density",
      "value": "1.8x",
      "comparator_or_baseline": "1.0x baseline",
      "interpretation": "CD8+ T-cell density increased approximately 1.8-fold, consistent with improved immune infiltration after sensitization strategies.",
      "evidence_line_refs": ["L33"]
    },
    {
      "id": "Q07",
      "metric": "Mouse sample size",
      "value": "78 mice efficacy / 84 mice controls",
      "comparator_or_baseline": "pooled across repeat experiments and matched controls",
      "interpretation": "The preclinical evidence combines substantial mouse observations from both efficacy and control arms to contrast treatment combinations and controls.",
      "evidence_line_refs": ["L19", "L20"]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "Vaccine-only treatment did not produce durable tumor control in non-immunogenic models.",
      "evidence_line_ref": "L36"
    },
    {
      "statement": "ICI-only treatment had limited activity in low-immunogenic baseline settings.",
      "evidence_line_ref": "L37"
    },
    {
      "statement": "Lipid particle controls lacking mRNA did not reproduce sensitization benefits.",
      "evidence_line_ref": "L38"
    },
    {
      "statement": "Blocking type I interferon signaling with anti-IFNAR1 eliminated the sensitization benefit.",
      "evidence_line_ref": "L39"
    }
  ]
}

{
  "claims": [
    {
      "claim_id": "C01",
      "claim": "Intratumoral mRNA vaccination can broaden tumor antigen presentation and increase immune visibility.",
      "strength": "strong",
