    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does TRAPPIST-1 b retain an atmosphere, as inferred from JWST MIRI thermal phase-curve spectroscopy and spectral structure near the 15 um CO2 band?",
  "datasets_or_cohorts": [
    {
      "id": "jwst-miri-phasecurve",
      "label": "JWST MIRI orbital phase-curve spectroscopy",
      "sample_size_or_observations": "One orbital phase curve with phase-resolved thermal spectra",
      "description": "Low-resolution JWST MIRI spectroscopy over an orbital phase curve, measuring thermal phase variation and spectral structure around the 15 um CO2 band for TRAPPIST-1 b."
    },
    {
      "id": "bare-rock-null",
      "label": "No-atmosphere (bare-rock) null scenario",
      "sample_size_or_observations": "One modeled scenario compared against the observed phase curve and spectrum",
      "description": "An atmosphere-free, bare-rock model with minimal heat redistribution used as a null scenario in retrieval comparisons against phase amplitude and spectral band behavior."
    },
    {
      "id": "co2-rich-retrieval",
      "label": "CO2-rich atmosphere retrieval family",
      "sample_size_or_observations": "One atmosphere-bearing retrieval family",
      "description": "Atmosphere model family emphasizing greenhouse-gas absorption and the 15 um CO2 band, used to interpret spectral structure and joint constraints from phase curve plus spectrum."
    },
    {
      "id": "o2-rich-retrieval",
      "label": "O2-rich atmosphere retrieval family",
      "sample_size_or_observations": "One oxidized-composition retrieval family",
      "description": "Atmosphere model family with oxidized composition and weaker CO2 signature constraints, evaluated as an alternative that is not fully ruled out by the current dataset."
    }
  ],
  "major_limitations": [
    "Single-planet study; does not establish prevalence across M-dwarf rocky planets.",
    "Retrieval assumptions (circulation, opacity priors, cloud treatment) affect posteriors.",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in the current dataset."
  ]
}

{
  "findings": [
