      "evidence_line_refs": [
        "L29"
      ]
    },
    {
      "id": "Q04",
      "metric": "15 um CO2 spectral band depth estimate",
      "value": "96 ppm",
      "comparator_or_baseline": "Flat-spectrum null",
      "interpretation": "Spectral structure near 15 um is consistent with atmospheric absorption relative to a flat-spectrum null.",
      "evidence_line_refs": [
