{
  "paper": {
    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does TRAPPIST-1 b retain an atmosphere, and can JWST phase-curve plus spectral data distinguish atmosphere-bearing from bare-rock scenarios?",
  "datasets_or_cohorts": [
    {
      "id": "observed-system",
      "label": "Observed TRAPPIST-1 b phase-curve dataset",
      "sample_size_or_observations": "One rocky exoplanet observed with phase-resolved thermal spectra",
      "description": "JWST MIRI low-resolution spectroscopy across orbital phase was used to measure thermal phase variation and spectral structure near 15 um."
    },
    {
      "id": "null-no-atmosphere",
      "label": "Bare-rock null scenario",
      "sample_size_or_observations": "Model comparator scenario",
      "description": "A no-atmosphere, minimal-redistribution case representing the bare-rock limit was used as a null model against atmospheric retrievals."
    },
    {
      "id": "co2-rich-atmosphere",
      "label": "CO2-rich atmosphere scenario",
      "sample_size_or_observations": "Model comparator scenario",
      "description": "Atmosphere-bearing retrieval models with significant greenhouse absorption were evaluated, including constraints from the 15 um CO2 region."
    },
    {
      "id": "o2-rich-atmosphere",
      "label": "O2-rich atmosphere scenario",
      "sample_size_or_observations": "Model comparator scenario",
      "description": "An oxidized-composition atmospheric branch with weaker CO2 signature constraints was considered and not fully excluded by the current data."
    }
  ],
  "major_limitations": [
    "This is a single-planet study and does not establish prevalence across rocky M-dwarf planets.",
    "Retrieval assumptions such as circulation, opacity priors, and cloud treatment affect posterior estimates.",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in the current dataset."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Dayside brightness temperature",
      "value": "342 +/- 22 K",
      "comparator_or_baseline": "Bare-rock expectation from no-redistribution model",
      "interpretation": "The dayside is warm, but this measurement alone does not prove a bare surface.",
      "evidence_line_refs": [
        "L27"
      ]
    },
    {
      "id": "Q02",
      "metric": "Heat transport efficiency (epsilon)",
      "value": "0.19 (+0.23 / -0.14)",
      "comparator_or_baseline": "0.0 for perfect no-atmosphere limit",
      "interpretation": "A non-zero redistribution parameter is favored over the strict no-atmosphere reradiation limit.",
      "evidence_line_refs": [
        "L28"
      ]
    },
    {
      "id": "Q03",
      "metric": "Bond albedo upper bound",
      "value": "<0.08",
      "comparator_or_baseline": "Higher-albedo reflective cases",
      "interpretation": "Energy-balance fits indicate a very dark planet rather than a highly reflective one.",
      "evidence_line_refs": [
        "L29"
      ]
    },
    {
      "id": "Q04",
      "metric": "15 um CO2 spectral band depth estimate",
      "value": "96 ppm",
      "comparator_or_baseline": "Flat-spectrum null",
      "interpretation": "Detected spectral structure is consistent with atmospheric absorption near the CO2 band.",
      "evidence_line_refs": [
        "L30"
      ]
    },
    {
      "id": "Q05",
      "metric": "Joint fit likelihood ratio (atmosphere vs no atmosphere)",
      "value": "7.4",
      "comparator_or_baseline": "1.0 baseline ratio",
      "interpretation": "The joint data products are better explained by atmosphere-bearing than no-atmosphere models.",
      "evidence_line_refs": [
        "L31"
      ]
    },
    {
      "id": "Q06",
      "metric": "Preferred pressure scale in CO2-rich retrieval family",
      "value": "0.3-1.0 bar",
      "comparator_or_baseline": "Near-vacuum branch",
      "interpretation": "Moderate-pressure CO2-rich solutions remain plausible in current retrieval families.",
      "evidence_line_refs": [
        "L32"
      ]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "A pure no-atmosphere model cannot simultaneously match both phase amplitude and spectral band behavior.",
      "evidence_line_ref": "L35"
    },
    {
      "statement": "O2-rich atmosphere cases are not fully ruled out by current data.",
      "evidence_line_ref": "L36"
    },
    {
      "statement": "Degeneracies between composition, clouds/hazes, and circulation assumptions remain significant.",
      "evidence_line_ref": "L37"
    }
  ]
}

{
  "claims": [
    {
      "claim_id": "C01",
      "claim": "Joint phase-curve and spectral evidence favors atmosphere-bearing explanations over a strictly airless model for TRAPPIST-1 b.",
      "strength": "moderate",
      "evidence": [
        {
          "quote": "Joint fit likelihood ratio (atmosphere vs no atmosphere) | 7.4 | 1.0 baseline ratio | Atmosphere-bearing models better explain combined data products",
          "line_ref": "L31"
