{
  "paper": {
    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does TRAPPIST-1 b retain an atmosphere, as inferred from JWST MIRI phase-curve thermal spectroscopy and retrieval modeling?",
  "datasets_or_cohorts": [
    {
      "id": "trappist-1b-phase-curve",
      "label": "TRAPPIST-1 b JWST MIRI phase curve",
      "sample_size_or_observations": "One planet; JWST MIRI low-resolution spectroscopy over an orbital phase curve",
      "description": "Phase-resolved thermal spectra and phase variation measurements used to constrain day-night heat transport and spectral structure near the 15 um CO2 band."
    },
    {
      "id": "bare-rock-null",
      "label": "Bare-rock, no-atmosphere null model",
      "sample_size_or_observations": "Model comparison against the observed phase-curve spectra",
      "description": "Atmosphere-free scenario with minimal heat redistribution, used as the null benchmark for fitting phase amplitude and spectral behavior."
    },
    {
      "id": "co2-rich-retrieval",
      "label": "CO2-rich atmosphere retrieval family",
      "sample_size_or_observations": "Model comparison against the observed phase-curve spectra",
      "description": "Atmosphere-bearing retrievals emphasizing CO2 absorption (including the 15 um band) and allowing for non-zero heat transport and moderate pressures."
    },
    {
      "id": "o2-rich-retrieval",
      "label": "O2-rich atmosphere retrieval family",
      "sample_size_or_observations": "Model comparison against the observed phase-curve spectra",
      "description": "Oxidized composition retrievals considered as alternative atmosphere-bearing cases, with weaker constraints on the CO2 signature."
    }
  ],
  "major_limitations": [
    "Single-planet study; cannot generalize to all M-dwarf rocky planets.",
    "Retrieval assumptions (circulation, opacity priors, clouds) affect posteriors.",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in this dataset.",
    "Degeneracies between composition, clouds/hazes, and circulation remain significant."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Dayside brightness temperature",
      "value": "342 +/- 22 K",
      "comparator_or_baseline": "Bare-rock expectation from no-redistribution model",
      "interpretation": "Dayside emission is warm, but this temperature alone does not uniquely indicate an airless surface.",
      "evidence_line_refs": ["L27"]
    },
    {
      "id": "Q02",
      "metric": "Heat transport efficiency (epsilon)",
      "value": "0.19 (+0.23 / -0.14)",
      "comparator_or_baseline": "0.0 for perfect no-atmosphere limit",
      "interpretation": "A non-zero heat redistribution parameter is preferred over the strict no-atmosphere limit.",
      "evidence_line_refs": ["L28"]
    },
    {
      "id": "Q03",
      "metric": "Bond albedo upper bound",
      "value": "<0.08",
      "comparator_or_baseline": "Higher-albedo reflective cases",
      "interpretation": "Energy-balance fits indicate the planet is very dark (low Bond albedo).",
      "evidence_line_refs": ["L29"]
    },
    {
      "id": "Q04",
      "metric": "15 um CO2 spectral band depth estimate",
      "value": "96 ppm",
      "comparator_or_baseline": "Flat-spectrum null",
      "interpretation": "Detected spectral structure near 15 um is consistent with atmospheric absorption.",
      "evidence_line_refs": ["L30"]
    },
    {
      "id": "Q05",
      "metric": "Joint fit likelihood ratio (atmosphere vs no atmosphere)",
      "value": "7.4",
      "comparator_or_baseline": "1.0 baseline ratio",
      "interpretation": "Atmosphere-bearing models better explain the combined phase-curve and spectral data products.",
      "evidence_line_refs": ["L31"]
    },
    {
      "id": "Q06",
      "metric": "Preferred pressure scale in CO2-rich retrieval family",
      "value": "0.3-1.0 bar",
      "comparator_or_baseline": "Near-vacuum branch",
      "interpretation": "Within CO2-rich retrievals, a moderate-pressure atmosphere remains plausible given current constraints.",
      "evidence_line_refs": ["L32"]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "A pure no-atmosphere model cannot simultaneously match both phase amplitude and spectral band behavior.",
      "evidence_line_ref": "L35"
    },
    {
      "statement": "O2-rich atmosphere cases are not fully ruled out by current data.",
      "evidence_line_ref": "L36"
    },
    {
      "statement": "Degeneracies between composition, clouds/hazes, and circulation assumptions remain significant.",
      "evidence_line_ref": "L37"
    }
  ]
}

