    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Whether TRAPPIST-1 b retains an atmosphere, using JWST MIRI phase-curve spectroscopy to jointly constrain day-night heat transport and atmospheric composition.",
  "datasets_or_cohorts": [
    {
      "id": "trappist-1b-phase-curve",
      "label": "Observed TRAPPIST-1 b phase-curve spectra",
      "sample_size_or_observations": "One rocky exoplanet observed with phase-resolved thermal spectra",
      "description": "Phase-resolved thermal spectra of TRAPPIST-1 b from JWST MIRI low-resolution spectroscopy over an orbital phase curve, including thermal phase variation and structure near the 15 um CO2 band."
    },
    {
      "id": "null-bare-rock",
      "label": "Bare-rock (no-atmosphere) scenario",
      "sample_size_or_observations": "Model comparator (airless, minimal heat redistribution)",
      "description": "A null scenario assuming a bare-rock, no-atmosphere model with minimal heat redistribution, compared against atmosphere-bearing retrieval models."
    },
    {
      "id": "co2-rich",
      "label": "CO2-rich atmosphere scenario",
      "sample_size_or_observations": "Model family in retrievals (CO2-rich atmosphere)",
      "description": "An atmosphere-bearing scenario with significant greenhouse gas absorption, evaluated via retrieval models against the observed phase-curve and spectral structure around the 15 um CO2 band."
    },
    {
      "id": "o2-rich",
      "label": "O2-rich atmosphere scenario",
      "sample_size_or_observations": "Model family in retrievals (oxidized atmosphere)",
      "description": "An oxidized (O2-rich) atmosphere scenario considered in retrievals, with weaker CO2 signature constraints and not fully ruled out by the current dataset."
    }
  ],
  "major_limitations": [
    "Single-planet study; does not establish prevalence across rocky M-dwarf planets.",
    "Retrieval assumptions (circulation, opacity priors, clouds) affect quantitative posteriors.",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in the current dataset.",
    "Degeneracies between composition, clouds/hazes, and circulation remain significant."
  ]
}

