{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Whether intratumoral delivery of an mRNA vaccine can create an interferon-rich inflammatory state that improves sensitivity to anti-PD-L1 therapy.",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy-combo",
      "label": "Mouse efficacy cohort (combination therapy)",
      "sample_size_or_observations": "78 tumor-bearing mice",
      "description": "Tumor-bearing mice pooled across repeat experiments to assess efficacy of intratumoral mRNA vaccine combined with anti-PD-L1 checkpoint blockade."
    },
    {
      "id": "mouse-controls",
      "label": "Mouse control cohort (vaccine only, ICI only, vehicle)",
      "sample_size_or_observations": "84 tumor-bearing mice",
      "description": "Matched control conditions pooled across experiments, including vaccine-only, ICI-only, and vehicle groups to contextualize combination effects."
    },
    {
      "id": "human-retrospective-metastatic",
      "label": "Human retrospective metastatic cohort",
      "sample_size_or_observations": "130 patients",
      "description": "Retrospective cohort of metastatic patients receiving immune checkpoint inhibitor treatment, stratified by prior SARS-CoV-2 mRNA vaccination status."
    },
    {
      "id": "human-vaccinated",
      "label": "Human vaccinated subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subset of the retrospective metastatic cohort with prior SARS-CoV-2 mRNA vaccination before or around immune checkpoint inhibitor treatment."
    },
    {
      "id": "human-unvaccinated",
      "label": "Human unvaccinated subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Subset of the retrospective metastatic cohort without prior SARS-CoV-2 mRNA vaccination, used as a comparator for outcomes under ICI treatment."
    }
  ],
  "major_limitations": [
    "Human analysis was retrospective and non-randomized, so confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Heterogeneity in tumor types and prior treatments limits causal inference.",
    "Mouse model findings may not translate directly to human tumors.",
    "Some mechanistic readouts were strongest in specific model systems."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Fraction of tumor proteins represented in immunopeptidome",
      "value": "62.3%",
      "comparator_or_baseline": "37.3%",
      "interpretation": "Intratumoral mRNA vaccination was associated with broader antigen display in the tumor immunopeptidome.",
      "evidence_line_refs": ["L28"]
    },
    {
      "id": "Q02",
      "metric": "Fraction of proteins represented in MHC-I peptidome",
      "value": "40.6%",
      "comparator_or_baseline": "20.6%",
      "interpretation": "The MHC-I-presented peptide landscape was expanded in the vaccine/combination context versus comparator.",
      "evidence_line_refs": ["L29"]
    },
    {
      "id": "Q03",
      "metric": "Retrospective human survival comparison p-value",
      "value": "p=0.01",
      "comparator_or_baseline": "N/A",
      "interpretation": "In the retrospective human comparison, the vaccinated subgroup showed improved survival under ICI treatment (statistical comparison reported).",
      "evidence_line_refs": ["L30"]
    },
    {
      "id": "Q04",
      "metric": "Type I interferon response score (normalized units)",
      "value": "3.1",
      "comparator_or_baseline": "1.0",
      "interpretation": "Tumors showed a marked induction of a type I interferon response after mRNA vaccination compared with baseline/comparator.",
      "evidence_line_refs": ["L31"]
    },
    {
      "id": "Q05",
      "metric": "PD-L1 expression fold-change in tumor tissue",
      "value": "2.4x",
      "comparator_or_baseline": "1.0x",
      "interpretation": "PD-L1 expression increased after vaccination, potentially increasing targetability of the PD-L1 checkpoint axis.",
      "evidence_line_refs": ["L32"]
    },
    {
      "id": "Q06",
      "metric": "Intratumoral CD8+ T-cell density fold-change",
      "value": "1.8x",
      "comparator_or_baseline": "1.0x",
      "interpretation": "Combination treatment was associated with increased intratumoral CD8+ T-cell density relative to comparator.",
      "evidence_line_refs": ["L33"]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "Vaccine-only treatment did not produce durable tumor control in non-immunogenic models.",
      "evidence_line_ref": "L36"
    },
    {
      "statement": "Lipid particle controls lacking mRNA did not reproduce the sensitization effect.",
      "evidence_line_ref": "L38"
    },
    {
      "statement": "Blocking type I interferon signaling with anti-IFNAR1 eliminated the observed sensitization benefit.",
      "evidence_line_ref": "L39"
    }
  ]
}

