{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Whether intratumoral mRNA vaccination can induce an interferon-rich tumor state that improves responsiveness to anti-PD-L1 immune checkpoint therapy.",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy-combination",
      "label": "Mouse efficacy cohort (combination therapy)",
      "sample_size_or_observations": "78 tumor-bearing mice",
      "description": "Pooled repeat-experiment mouse cohort used to evaluate antitumor efficacy of intratumoral mRNA vaccine plus anti-PD-L1 treatment."
    },
    {
      "id": "mouse-matched-controls",
      "label": "Mouse matched control cohort",
      "sample_size_or_observations": "84 tumor-bearing mice",
      "description": "Matched controls including vaccine-only, ICI-only, and vehicle conditions used to benchmark combination effects in murine models."
    },
    {
      "id": "human-retrospective-metastatic",
      "label": "Human retrospective metastatic cohort",
      "sample_size_or_observations": "130 patients total",
      "description": "Retrospective translational cohort of metastatic patients treated with ICI, stratified by prior SARS-CoV-2 mRNA vaccination status."
    },
    {
      "id": "human-vaccinated-subgroup",
      "label": "Human vaccinated subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subset of retrospective metastatic patients with prior SARS-CoV-2 mRNA vaccination before or around ICI treatment window."
    },
    {
      "id": "human-unvaccinated-subgroup",
      "label": "Human unvaccinated subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Comparator subset of retrospective metastatic patients without prior SARS-CoV-2 mRNA vaccination in the analyzed cohort."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized, so residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients, complicating interpretation.",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors.",
    "Some mechanistic readouts were strongest in specific model systems rather than uniformly across all tested settings."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Fraction of tumor proteins represented in immunopeptidome",
      "value": "62.3%",
      "comparator_or_baseline": "37.3%",
      "interpretation": "Intratumoral mRNA vaccination was associated with broader antigen display.",
      "evidence_line_refs": [
        "L28"
      ]
    },
    {
      "id": "Q02",
      "metric": "Fraction of proteins represented in MHC-I peptidome",
      "value": "40.6%",
      "comparator_or_baseline": "20.6%",
      "interpretation": "The MHC-I-presented peptide landscape expanded substantially after vaccination.",
      "evidence_line_refs": [
        "L29"
      ]
    },
    {
      "id": "Q03",
      "metric": "Retrospective human survival comparison p-value",
      "value": "p=0.01",
      "comparator_or_baseline": "N/A",
      "interpretation": "Vaccinated patients had better survival under ICI in retrospective comparison.",
      "evidence_line_refs": [
        "L30"
      ]
    },
    {
      "id": "Q04",
      "metric": "Type I interferon response score (normalized units)",
      "value": "3.1",
      "comparator_or_baseline": "1.0",
      "interpretation": "Tumors showed marked induction of type I interferon signaling after mRNA vaccination.",
      "evidence_line_refs": [
        "L31"
      ]
    },
    {
      "id": "Q05",
      "metric": "PD-L1 expression fold-change in tumor tissue",
      "value": "2.4x",
      "comparator_or_baseline": "1.0x",
      "interpretation": "Checkpoint-pathway expression increased, supporting combination with PD-L1 blockade.",
      "evidence_line_refs": [
        "L32"
      ]
    },
    {
      "id": "Q06",
      "metric": "Intratumoral CD8+ T-cell density fold-change",
      "value": "1.8x",
      "comparator_or_baseline": "1.0x",
      "interpretation": "Combination treatment increased intratumoral cytotoxic T-cell infiltration.",
      "evidence_line_refs": [
        "L33"
      ]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "Vaccine-only treatment did not produce durable tumor control in non-immunogenic models.",
      "evidence_line_ref": "L36"
    },
    {
      "statement": "ICI-only treatment had limited activity in low-immunogenic baseline settings.",
      "evidence_line_ref": "L37"
    },
    {
      "statement": "Lipid particle controls lacking mRNA did not reproduce the sensitization effect.",
      "evidence_line_ref": "L38"
    },
    {
      "statement": "Blocking type I interferon signaling with anti-IFNAR1 eliminated the observed sensitization benefit.",
      "evidence_line_ref": "L39"
    }
  ]
}

