{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Does intratumoral delivery of an mRNA vaccine create an interferon-rich inflammatory tumor state that improves sensitivity to immune checkpoint blockade (anti-PD-L1)?",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy",
      "label": "Mouse efficacy cohort (combination therapy)",
      "sample_size_or_observations": "78 tumor-bearing mice",
      "description": "Murine tumor models treated with intratumoral mRNA vaccine in combination with anti-PD-L1, pooled across repeat experiments to assess anti-tumor efficacy."
    },
    {
      "id": "mouse-controls",
      "label": "Mouse control cohort (single agents/vehicle)",
      "sample_size_or_observations": "84 tumor-bearing mice",
      "description": "Matched murine controls including vaccine only, ICI only, and vehicle arms pooled across experiments to benchmark baseline activity and specificity of effects."
    },
    {
      "id": "human-retrospective",
      "label": "Human retrospective metastatic cohort",
      "sample_size_or_observations": "130 patients",
      "description": "Retrospective cohort of metastatic patients receiving immune checkpoint inhibitor treatment, stratified by prior SARS-CoV-2 mRNA vaccination status for outcome comparison."
    },
    {
      "id": "human-vaccinated",
      "label": "Human vaccinated subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subset of the retrospective metastatic cohort with prior SARS-CoV-2 mRNA vaccination, used for survival comparison under ICI treatment."
    },
    {
      "id": "human-unvaccinated",
      "label": "Human unvaccinated subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Subset of the retrospective metastatic cohort without prior SARS-CoV-2 mRNA vaccination, serving as the comparator group for survival under ICI treatment."
    }
  ],
  "major_limitations": [
    "Human analysis is retrospective and non-randomized; residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors.",
    "Some mechanistic readouts were strongest in specific model systems rather than uniformly across all tested settings."
  ]
}

{
  "findings": [
