            "evidence_line_refs": ["L30"]
        },
        {
            "id": "Q04",
            "metric": "Type I interferon response score (normalized units)",
            "value": "3.1",
            "comparator_or_baseline": "1.0",
            "interpretation": "Marked induction after mRNA vaccination in tumors",
            "evidence_line_refs": ["L31"]
        },
        {
            "id": "Q05",
            "metric": "PD-L1 expression fold-change in tumor tissue",
            "value": "2.4x",
            "comparator_or_baseline": "1.0x",
            "interpretation": "Checkpoint axis became more targetable",
            "evidence_line_refs": ["L32"]
        },
        {
            "id": "Q06",
            "metric": "Intratumoral CD8+ T-cell density fold-change",
            "value": "1.8x",
            "comparator_or_baseline": "1.0x",
            "interpretation": "Increased immune infiltration with combination therapy",
            "evidence_line_refs": ["L33"]
        }
    ],
    "controls_or_null_results": [
        {
            "statement": "Vaccine-only treatment did not produce durable tumor control in non-immunogenic models.",
            "evidence_line_ref": "L36"
        },
        {
            "statement": "ICI-only treatment had limited activity in low-immunogenic baseline settings.",
            "evidence_line_ref": "L37"
        },
        {
            "statement": "Lipid particle controls lacking mRNA did not reproduce the sensitization effect.",
            "evidence_line_ref": "L38"
        },
        {
            "statement": "Blocking type I interferon signaling with anti-IFNAR1 eliminated the observed sensitization benefit.",
            "evidence_line_ref": "L39"
        }
    ]
}

