{
    "paper": {
        "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
        "doi": "10.1038/s41586-025-09006-8",
        "publication_year": 2025,
        "domain": "oncology-immunology",
        "study_type": "preclinical+retrospective"
    },
    "core_question": "Investigates whether intratumoral delivery of an mRNA vaccine can create an interferon-rich inflammatory state that improves sensitivity to anti-PD-L1 therapy.",
    "datasets_or_cohorts": [
        {
            "id": "mouse-efficacy-cohort",
            "label": "Mouse efficacy cohort (combination therapy)",
            "sample_size_or_observations": "78 tumor-bearing mice pooled across repeat experiments",
            "description": "78 tumor-bearing mice in murine tumor models with intratumoral mRNA vaccine and anti-PD-L1 combinations."
        },
        {
            "id": "mouse-control-cohort",
            "label": "Mouse control cohort (vaccine only, ICI only, and vehicle)",
            "sample_size_or_observations": "84 tumor-bearing mice pooled across matched controls",
            "description": "84 tumor-bearing mice in matched controls for vaccine only, ICI only, and vehicle."
        },
        {
            "id": "human-retrospective-metastatic-cohort",
            "label": "Human retrospective metastatic cohort",
            "sample_size_or_observations": "130 patients total",
            "description": "A retrospective cohort of 130 metastatic patients receiving ICI treatment, compared by prior SARS-CoV-2 mRNA vaccination status."
        },
        {
            "id": "human-vaccinated-subgroup",
            "label": "Human vaccinated subgroup",
            "sample_size_or_observations": "43 patients",
            "description": "A subgroup of the human retrospective cohort of 43 patients who had received prior SARS-CoV-2 mRNA vaccination."
        },
        {
            "id": "human-unvaccinated-subgroup",
            "label": "Human unvaccinated subgroup",
            "sample_size_or_observations": "87 patients",
            "description": "A subgroup of the human retrospective cohort of 87 patients who had not received prior SARS-CoV-2 mRNA vaccination."
        }
    ],
    "major_limitations": [
        "The human analysis is retrospective and non-randomized; residual confounding is likely.",
        "Vaccination timing relative to ICI initiation varied across patients.",
        "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation.",
        "Mouse model effects may not map one-to-one to all human tumors.",
        "Some mechanistic readouts were strongest in specific model systems rather than uniformly across all tested settings."
    ]
}

{
    "findings": [
        {
            "id": "Q01",
            "metric": "Fraction of tumor proteins represented in immunopeptidome",
            "value": "62.3%",
            "comparator_or_baseline": "37.3%",
            "interpretation": "Broader antigen display after intratumoral mRNA vaccination",
            "evidence_line_refs": [
