{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Does intratumoral delivery of an mRNA vaccine create an interferon-rich inflammatory state that improves sensitivity to anti-PD-L1 therapy?",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy",
      "label": "Mouse Efficacy Cohort",
      "sample_size_or_observations": "78 mice",
      "description": "Preclinical arm with multiple murine tumor models receiving intratumoral mRNA vaccine and anti-PD-L1 combinations."
    },
    {
      "id": "mouse-control",
      "label": "Mouse Control Cohort",
      "sample_size_or_observations": "84 mice",
      "description": "Preclinical control arm receiving vaccine only, ICI only, or vehicle, pooled across matched controls."
    },
    {
      "id": "human-retrospective-total",
      "label": "Human Retrospective Cohort",
      "sample_size_or_observations": "130 patients",
      "description": "Retrospective cohort of metastatic patients receiving ICI treatment, compared by prior SARS-CoV-2 mRNA vaccination status."
    },
    {
      "id": "human-vaccinated",
      "label": "Human Vaccinated Subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subgroup of the retrospective cohort consisting of patients who received SARS-CoV-2 mRNA vaccination."
    },
    {
      "id": "human-unvaccinated",
      "label": "Human Unvaccinated Subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Subgroup of the retrospective cohort consisting of patients who did not receive SARS-CoV-2 mRNA vaccination."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized; residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors.",
    "Some mechanistic readouts were strongest in specific model systems rather than uniformly across all tested settings."
  ]
}

