{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Can intratumoral delivery of an mRNA vaccine create an interferon-rich inflammatory state that improves sensitivity to anti-PD-L1 therapy?",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy",
      "label": "Mouse efficacy cohort",
      "sample_size_or_observations": "78 mice",
      "description": "Tumor-bearing mice pooled across repeat experiments receiving combination therapy."
    },
    {
      "id": "mouse-control",
      "label": "Mouse control cohort",
      "sample_size_or_observations": "84 mice",
      "description": "Tumor-bearing mice pooled across matched controls (vaccine only, ICI only, and vehicle)."
    },
    {
      "id": "human-retrospective",
      "label": "Human retrospective cohort",
      "sample_size_or_observations": "130 patients",
      "description": "Total retrospective cohort of metastatic patients receiving ICI treatment."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized; residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors."
  ]
}

