    {
      "claim_id": "C01",
      "claim": "Intratumoral mRNA vaccination broadens the MHC-I antigen repertoire.",
      "strength": "strong",
      "evidence": [
        {
          "quote": "Fraction of proteins represented in MHC-I peptidome | 40.6% | 20.6%",
          "line_ref": "L29"
