    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does the Earth-sized, tidally locked exoplanet TRAPPIST-1 b retain any atmosphere under the strong stellar irradiation from its M dwarf host star?",
  "datasets_or_cohorts": [
    {
      "id": "observed-system",
      "label": "Observed System",
      "sample_size_or_observations": "Phase-resolved thermal spectra",
      "description": "The actual measurement data from JWST MIRI low-resolution spectroscopy capturing the thermal emission across the orbital phase of the exoplanet TRAPPIST-1 b."
    },
    {
      "id": "null-scenario",
      "label": "Bare-Rock Null Scenario",
      "sample_size_or_observations": "One theoretical model",
      "description": "A computational model of the planet assuming a completely airless, bare-rock surface with minimal heat redistribution, used as the baseline for comparison."
    },
    {
      "id": "co2-rich-atmosphere",
      "label": "CO2-rich Atmosphere Model",
      "sample_size_or_observations": "One theoretical model",
      "description": "Atmosphere retrieval model family with an assumed composition rich in carbon dioxide, predicting significant greenhouse gas absorption and heat transport."
    },
    {
      "id": "o2-rich-atmosphere",
      "label": "O2-rich Atmosphere Model",
      "sample_size_or_observations": "One theoretical model",
      "description": "Atmosphere retrieval model family assuming an oxidized composition, used to test alternative atmospheric chemistry scenarios and constraints."
    }
  ],
  "major_limitations": [
    "This is a single-planet study and does not establish atmospheric prevalence across all M-dwarf rocky planets.",
    "Retrieval assumptions (circulation, opacity priors, cloud treatment) affect quantitative posteriors.",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in the current dataset."
  ]
}

