{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Does intratumoral delivery of an mRNA vaccine create an interferon-rich inflammatory state that improves tumor sensitivity to anti-PD-L1 therapy?",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy-combo",
      "label": "Mouse Efficacy Cohort (Combination)",
      "sample_size_or_observations": "78",
      "description": "Tumor-bearing mice receiving intratumoral mRNA vaccine and anti-PD-L1 combination therapy, pooled across repeat experiments."
    },
    {
      "id": "mouse-controls",
      "label": "Mouse Control Cohort",
      "sample_size_or_observations": "84",
      "description": "Tumor-bearing mice pooled across matched controls including vaccine only, ICI only, and vehicle treatments."
    },
    {
      "id": "human-retrospective",
      "label": "Human Retrospective Cohort",
      "sample_size_or_observations": "130",
      "description": "Metastatic patients receiving ICI treatment, retrospectively analyzed based on prior SARS-CoV-2 mRNA vaccination status (43 vaccinated, 87 unvaccinated)."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized; residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors."
  ]
}

{
