  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "The paper investigates whether intratumoral delivery of an mRNA vaccine can create an interferon-rich inflammatory state that improves sensitivity to anti-PD-L1 therapy.",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy-combo",
      "label": "Mouse Efficacy Cohort (Combination)",
      "sample_size_or_observations": "78 tumor-bearing mice",
      "description": "Mice treated with the combination of intratumoral mRNA vaccine and anti-PD-L1 therapy across repeat experiments to evaluate efficacy. (L19)"
    },
    {
      "id": "human-retrospective-vaccinated",
      "label": "Human Retrospective Vaccinated Subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Metastatic patients receiving ICI treatment who had prior SARS-CoV-2 mRNA vaccination. (L22, L15)"
    },
    {
      "id": "human-retrospective-unvaccinated",
      "label": "Human Retrospective Unvaccinated Subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Metastatic patients receiving ICI treatment who had no prior SARS-CoV-2 mRNA vaccination, used as a comparator. (L23, L15)"
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized; residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients, adding heterogeneity to the retrospective data.",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation in the human cohort.",
    "Mouse model effects may not map one-to-one to all human tumors, limiting direct translational certainty."
  ]
}

