{
  "claims": [
    {
      "claim_id": "C01",
      "claim": "Intratumoral mRNA vaccination significantly broadens the repertoire of tumor antigens presented on MHC-I molecules.",
      "strength": "strong",
      "evidence": [
        {
          "quote": "Fraction of tumor proteins represented in immunopeptidome | 62.3% | 37.3% | Broader antigen display after intratumoral mRNA vaccination",
