{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "mixed"
  },
  "core_question": "Does intratumoral delivery of an mRNA vaccine sensitize non-responsive tumors to anti-PD-L1 therapy by creating an interferon-rich inflammatory state?",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy-combo",
      "label": "Mouse Combination Efficacy Cohort",
      "sample_size_or_observations": "78 tumor-bearing mice",
      "description": "Mice pooled from repeat preclinical experiments, treated with intratumoral mRNA vaccine and anti-PD-L1 combination therapy."
    },
    {
      "id": "mouse-control",
      "label": "Mouse Preclinical Control Cohorts",
      "sample_size_or_observations": "84 tumor-bearing mice",
      "description": "Mice used for vaccine-only, ICI-only, and vehicle control treatments to establish baseline and monotherapy activity."
    },
    {
      "id": "human-retrospective-metastatic",
      "label": "Human Retrospective Metastatic Cohort",
      "sample_size_or_observations": "130 patients total",
      "description": "Retrospective cohort of metastatic cancer patients receiving ICI treatment, stratified by prior SARS-CoV-2 mRNA vaccination status."
    },
    {
      "id": "human-vaccinated-subgroup",
      "label": "Human Vaccinated Subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subgroup of metastatic ICI-treated patients who had received the SARS-CoV-2 mRNA vaccine prior to ICI initiation for survival comparison."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized; residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors."
  ]
}

