    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does TRAPPIST-1 b, an Earth-sized rocky planet on a short tidally locked orbit around an M dwarf, retain any atmosphere under strong stellar irradiation?",
  "datasets_or_cohorts": [
    {
      "id": "observed-system",
      "label": "TRAPPIST-1 b phase-resolved thermal spectra",
      "sample_size_or_observations": "1 rocky exoplanet with phase-resolved thermal spectra",
      "description": "Observed system: one rocky exoplanet (TRAPPIST-1 b) with phase-resolved thermal spectra from JWST MIRI low-resolution spectroscopy over an orbital phase curve."
    },
    {
      "id": "null-scenario",
      "label": "Bare-rock no-atmosphere model",
      "sample_size_or_observations": "1 theoretical model scenario",
      "description": "Null scenario: bare-rock, no-atmosphere model with minimal heat redistribution, used as baseline for comparison."
    },
    {
      "id": "co2-atmosphere",
      "label": "CO2-rich atmosphere scenario",
      "sample_size_or_observations": "1 atmospheric model scenario",
      "description": "CO2-rich atmosphere scenario: atmosphere model with significant greenhouse gas absorption, particularly probing the 15 um CO2 band."
    },
    {
      "id": "o2-atmosphere",
      "label": "O2-rich atmosphere scenario",
      "sample_size_or_observations": "1 atmospheric model scenario",
      "description": "O2-rich atmosphere scenario: oxidized composition with weaker CO2 signature constraints, not fully ruled out by current data."
    }
  ],
  "major_limitations": [
    "This is a single-planet study and does not establish atmospheric prevalence across all M-dwarf rocky planets.",
    "Retrieval assumptions (circulation, opacity priors, cloud treatment) affect quantitative posteriors.",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in the current dataset.",
    "Degeneracies between composition, clouds/hazes, and circulation assumptions remain significant."
  ]
}

