{
  "paper": {
    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does the rocky, likely tidally locked exoplanet TRAPPIST-1 b retain an atmosphere, as inferred from JWST MIRI thermal phase-curve spectroscopy and the 15 µm CO2 band?",
  "datasets_or_cohorts": [
    {
      "id": "jwst-miri-phase-curve",
      "label": "JWST/MIRI phase-curve spectroscopy",
      "sample_size_or_observations": "One planet (TRAPPIST-1 b) observed over an orbital phase curve with phase-resolved thermal spectra",
      "description": "Low-resolution JWST MIRI spectroscopy spanning an orbital phase curve, measuring thermal phase variation and spectral structure near the ~15 µm CO2 band to constrain day–night heat transport and possible atmospheric absorption."
    },
    {
      "id": "retrieval-model-comparisons",
      "label": "Atmosphere-free vs atmosphere-bearing retrieval scenarios",
      "sample_size_or_observations": "Multiple model families (bare-rock/no-atmosphere, CO2-rich atmosphere, O2-rich atmosphere) fit to the same phase-curve dataset",
      "description": "A retrieval workflow compares a null bare-rock, minimal-redistribution model against atmosphere-bearing cases (including CO2-rich and O2-rich compositions) to determine which scenarios best explain the combined phase-curve amplitude/offsets and spectral behavior."
    }
  ],
  "major_limitations": [
    "Single-planet study; does not establish how common atmospheres are among M-dwarf rocky planets.",
    "Quantitative posteriors depend on retrieval assumptions (circulation, opacity priors, clouds/hazes).",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in the current dataset."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Dayside brightness temperature",
      "value": "342 +/- 22 K",
      "comparator_or_baseline": "Bare-rock expectation from no-redistribution model",
      "interpretation": "The dayside is warm, but this temperature alone does not uniquely identify the surface/atmosphere state.",
      "evidence_line_refs": ["L27"]
    },
    {
      "id": "Q02",
      "metric": "Heat transport efficiency (epsilon)",
      "value": "0.19 (+0.23 / -0.14)",
      "comparator_or_baseline": "0.0 for perfect no-atmosphere limit",
      "interpretation": "A non-zero heat redistribution parameter is preferred, suggesting some mechanism transports energy away from the dayside.",
      "evidence_line_refs": ["L28"]
    },
    {
      "id": "Q03",
      "metric": "Bond albedo upper bound",
      "value": "<0.08",
      "comparator_or_baseline": "Higher-albedo reflective cases",
      "interpretation": "Energy-balance fits prefer a very dark planet, limiting highly reflective scenarios.",
      "evidence_line_refs": ["L29"]
    },
    {
      "id": "Q04",
      "metric": "15 um CO2 spectral band depth estimate",
      "value": "96 ppm",
      "comparator_or_baseline": "Flat-spectrum null",
      "interpretation": "Detected spectral structure near 15 µm is consistent with atmospheric absorption rather than a featureless spectrum.",
      "evidence_line_refs": ["L30"]
    },
    {
      "id": "Q05",
      "metric": "Joint fit likelihood ratio (atmosphere vs no atmosphere)",
      "value": "7.4",
      "comparator_or_baseline": "1.0 baseline ratio",
      "interpretation": "Atmosphere-bearing models better explain the combined phase-curve and spectral data products than the no-atmosphere alternative.",
      "evidence_line_refs": ["L31"]
    },
    {
      "id": "Q06",
      "metric": "Preferred pressure scale in CO2-rich retrieval family",
      "value": "0.3-1.0 bar",
      "comparator_or_baseline": "Near-vacuum branch",
      "interpretation": "Within CO2-rich model families, moderate-pressure atmospheres remain plausible given the current data.",
      "evidence_line_refs": ["L32"]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "A pure no-atmosphere model cannot simultaneously match both phase amplitude and spectral band behavior.",
      "evidence_line_ref": "L35"
    },
    {
      "statement": "O2-rich atmosphere cases are not fully ruled out by current data.",
      "evidence_line_ref": "L36"
    },
    {
      "statement": "Degeneracies between composition, clouds/hazes, and circulation assumptions remain significant.",
      "evidence_line_ref": "L37"
    }
  ]
}

{
  "claims": [
    {
      "claim_id": "C01",
      "claim": "The phase-curve retrieval favors non-zero day–night heat redistribution for TRAPPIST-1 b, inconsistent with the strict no-atmosphere (no-transport) limit.",
      "strength": "moderate",
      "evidence": [
        {
          "quote": "Heat transport efficiency (epsilon) | 0.19 (+0.23 / -0.14) | 0.0 for perfect no-atmosphere limit | Non-zero redistribution is favored",
          "line_ref": "L28"
        },
        {
          "quote": "A non-zero redistribution parameter implies transport beyond an immediate bare-rock reradiation limit.",
          "line_ref": "L41"
        }
      ],
      "caveat": "Quantitative inferences depend on retrieval assumptions (e.g., circulation and priors), and night-side constraints are weaker in this dataset."
    },
    {
      "claim_id": "C02",
      "claim": "Spectral structure near 15 µm provides evidence consistent with atmospheric absorption, especially in CO2-rich scenarios.",
      "strength": "tentative",
      "evidence": [
        {
          "quote": "15 um CO2 spectral band depth estimate | 96 ppm | Flat-spectrum null | Spectral structure is consistent with atmospheric absorption",
          "line_ref": "L30"
        },
        {
          "quote": "Spectral structure near 15 um provides composition-sensitive evidence, especially for CO2-rich scenarios.",
          "line_ref": "L42"
        }
      ],
      "caveat": "Weak spectral features can be biased by stellar variability and instrument systematics, and degeneracies with clouds/hazes and circulation remain significant."
    },
    {
      "claim_id": "C03",
      "claim": "Jointly fitting the phase curve and spectrum favors atmosphere-bearing models over a no-atmosphere interpretation for TRAPPIST-1 b.",
      "strength": "moderate",
      "evidence": [
        {
          "quote": "Joint fit likelihood ratio (atmosphere vs no atmosphere) | 7.4 | 1.0 baseline ratio | Atmosphere-bearing models better explain combined data products",
          "line_ref": "L31"
        },
        {
          "quote": "Joint constraints from phase curve and spectrum are stronger than either observable alone.",
          "line_ref": "L43"
        },
        {
          "quote": "The dataset provides meaningful evidence against a strictly airless interpretation for TRAPPIST-1 b, but atmospheric composition and pressure remain uncertain enough that follow-up spectroscopy is essential.",
          "line_ref": "L53"
        }
      ],
      "caveat": "This is a single-planet study and retrieval/systematics assumptions could shift the inferred preference between atmosphere and bare-rock models."
    },
    {
      "claim_id": "C04",
      "claim": "If TRAPPIST-1 b has a CO2-rich atmosphere, current retrievals allow a moderate-pressure solution (roughly sub-bar to ~1 bar), but alternative compositions (e.g., O2-rich) are not excluded.",
      "strength": "tentative",
      "evidence": [
        {
          "quote": "Preferred pressure scale in CO2-rich retrieval family | 0.3-1.0 bar | Near-vacuum branch | Moderate-pressure atmosphere family remains plausible",
          "line_ref": "L32"
        },
        {
          "quote": "O2-rich atmosphere cases are not fully ruled out by current data.",
          "line_ref": "L36"
        }
      ],
      "caveat": "Degeneracies between composition, clouds/hazes, and circulation assumptions remain significant, limiting how uniquely pressure and composition can be determined."
    }
  ]
}

{
  "headline": "JWST phase-curve data hint that TRAPPIST-1 b may not be airless",
  "plain_language_summary": "TRAPPIST-1 b is a hot, Earth-sized rocky planet that likely always shows the same face to its star. A central question is whether such a strongly irradiated world can keep any atmosphere, or whether it has been stripped down to bare rock. In this study, researchers used JWST’s MIRI instrument to observe the planet’s infrared light over an entire orbit (a phase curve). They compared models with no atmosphere to models that include an atmosphere and heat transport from day to night. The results favor some non-zero heat redistribution and show spectral structure near the 15 micrometer carbon-dioxide band, both of which are consistent with an atmosphere. However, the exact atmospheric composition and pressure are still uncertain, and the authors emphasize that follow-up observations are needed.",
  "what_is_new": [
    "Uses JWST/MIRI low-resolution phase-curve spectroscopy to probe day–night heat transport.",
    "Finds a preferred non-zero heat redistribution (epsilon) relative to the no-atmosphere limit.",
    "Reports spectral structure near the ~15 µm CO2 band consistent with atmospheric absorption."
  ],
  "why_caution_is_needed": [
    "It is a single-planet study and does not show how common atmospheres are on similar worlds.",
    "Model retrieval results depend on assumptions about circulation, opacities, and clouds/hazes.",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side emission is less well constrained than day-side emission in this dataset."
  ],
  "glossary": [
    {
      "term": "Phase curve",
      "definition": "Measurements of a planet’s brightness over its orbit, revealing how the emitted/received light changes with viewing angle."
    },
    {
      "term": "Brightness temperature",
      "definition": "The temperature a blackbody would need to match the observed infrared brightness at a given wavelength band."
    },
    {
      "term": "Bond albedo",
      "definition": "The fraction of total incoming starlight that a planet reflects back to space, averaged over all angles and wavelengths."
    },
    {
      "term": "Retrieval model",
      "definition": "A statistical framework that fits physical atmosphere/surface models to data to estimate parameters like composition and pressure."
    },
    {
      "term": "15 µm CO2 band",
      "definition": "A strong infrared absorption feature of carbon dioxide near 15 micrometers that can imprint structure in thermal spectra."
    }
  ],
  "open_questions": [
    "What is the planet’s true atmospheric composition (CO2-rich, O2-rich, or something else)?",
    "What atmospheric pressure range is allowed once additional wavelengths and repeated observations are included?",
    "How much do clouds/hazes and circulation assumptions shift the inferred day–night heat transport?",
    "Can future observations better constrain the night-side emission and confirm the weak spectral feature?"
  ]
}

