{
  "paper": {
    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Whether TRAPPIST-1 b retains any atmosphere under strong stellar irradiation, using JWST phase-curve spectroscopy to jointly constrain heat transport and composition.",
  "datasets_or_cohorts": [
    {
      "id": "trappist1b-phasecurve",
      "label": "TRAPPIST-1 b JWST MIRI phase-curve spectroscopy",
      "sample_size_or_observations": "One planet observed over an orbital phase curve with phase-resolved thermal spectra",
      "description": "Thermal phase variation and spectral structure around the 15 um CO2 band are measured with JWST MIRI low-resolution spectroscopy to infer atmospheric and heat-transport properties."
    },
    {
      "id": "no-atmosphere-null",
      "label": "Bare-rock, no-atmosphere model (null scenario)",
      "sample_size_or_observations": "Model comparator for the same phase-curve dataset",
      "description": "An atmosphere-free interpretation assuming minimal heat redistribution is used as a baseline to test whether an airless surface can explain both the phase amplitude and spectral behavior."
    },
    {
      "id": "co2-rich-retrieval",
      "label": "CO2-rich atmosphere retrieval family",
      "sample_size_or_observations": "Model family fit to phase-curve + spectrum",
      "description": "Atmosphere-bearing retrieval models with significant greenhouse gas absorption are compared against the null to assess whether CO2-related spectral structure and redistribution are supported."
    },
    {
      "id": "o2-rich-retrieval",
      "label": "O2-rich atmosphere retrieval family",
      "sample_size_or_observations": "Model family fit to phase-curve + spectrum",
      "description": "Oxidized-composition atmosphere scenarios with weaker CO2 signature constraints are evaluated as alternative explanations that may not be fully excluded by the present data."
    }
  ],
  "major_limitations": [
    "Single-planet study; does not establish prevalence across M-dwarf rocky planets.",
    "Retrieval assumptions (circulation, opacity priors, cloud treatment) affect posteriors.",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in the current dataset.",
    "Degeneracies between composition, clouds/hazes, and circulation assumptions remain significant."
  ]
}

