  "findings": [
    {
      "id": "Q01",
      "metric": "Dayside brightness temperature",
      "value": "342 +/- 22 K",
      "comparator_or_baseline": "Bare-rock expectation from no-redistribution model",
      "interpretation": "The retrieved dayside temperature indicates a warm dayside, but the authors note it is not alone decisive for distinguishing a bare surface from an atmosphere-bearing case.",
      "evidence_line_refs": ["L27"]
    },
    {
      "id": "Q02",
      "metric": "Heat transport efficiency (epsilon)",
      "value": "0.19 (+0.23 / -0.14)",
      "comparator_or_baseline": "0.0 for perfect no-atmosphere limit",
      "interpretation": "A non-zero redistribution parameter is favored, consistent with some day-to-night transport beyond a strict no-atmosphere reradiation limit.",
      "evidence_line_refs": ["L28"]
    },
    {
      "id": "Q03",
      "metric": "Bond albedo upper bound",
      "value": "<0.08",
      "comparator_or_baseline": "Higher-albedo reflective cases",
