{
  "paper": {
    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does TRAPPIST-1 b retain an atmosphere (versus being a bare rock), as inferred from JWST MIRI thermal phase-curve spectroscopy including structure near the 15 um CO2 band?",
  "datasets_or_cohorts": [
    {
      "id": "observed-system",
      "label": "Observed TRAPPIST-1 b phase curve",
      "sample_size_or_observations": "1 planet; JWST MIRI low-resolution spectroscopy over an orbital phase curve",
      "description": "Phase-resolved thermal spectra and phase-dependent brightness of TRAPPIST-1 b used to constrain day-night heat transport and spectral features."
    },
    {
      "id": "null-bare-rock",
      "label": "Null scenario: no atmosphere",
      "sample_size_or_observations": "Model comparison against the observed phase-curve and spectral structure",
      "description": "Bare-rock, no-atmosphere model intended to represent minimal heat redistribution and a flat/featureless spectrum baseline."
    },
    {
      "id": "co2-rich-atmosphere",
      "label": "CO2-rich atmosphere scenario",
      "sample_size_or_observations": "Retrieval family evaluated against the same phase-curve + spectrum",
      "description": "Atmosphere-bearing retrieval family with significant greenhouse gas absorption, targeting a detectable feature near the 15 um CO2 band."
    },
    {
      "id": "o2-rich-atmosphere",
      "label": "O2-rich atmosphere scenario",
      "sample_size_or_observations": "Alternative oxidized-composition retrieval family",
      "description": "Atmosphere scenario with oxidized composition in which CO2 constraints are weaker, explored as a viable alternative given current data."
    }
  ],
  "major_limitations": [
    "Single-planet target; results do not establish prevalence across M-dwarf rocky planets.",
    "Retrieval assumptions about circulation, opacity priors, and clouds affect quantitative posteriors.",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in the current dataset.",
    "Degeneracies between composition, clouds/hazes, and circulation assumptions remain significant."
  ]
}

