{
  "paper": {
    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does the Earth-sized rocky exoplanet TRAPPIST-1 b retain an atmosphere, as inferred from JWST MIRI phase-curve thermal spectroscopy and structure near the 15 um CO2 band?",
  "datasets_or_cohorts": [
    {
      "id": "trappist-1b-phase-curve",
      "label": "JWST MIRI phase-curve spectroscopy",
      "sample_size_or_observations": "Phase-resolved thermal spectra over one orbital phase curve of TRAPPIST-1 b",
      "description": "Low-resolution JWST/MIRI spectroscopy taken over the planet's orbital phase, measuring thermal flux variations and spectral structure across the orbit."
    },
    {
      "id": "null-bare-rock-model",
      "label": "No-atmosphere (bare-rock) null scenario",
      "sample_size_or_observations": "Model comparison case (airless planet; minimal heat redistribution)",
      "description": "An atmosphere-free, bare-rock forward model used as a null hypothesis, representing the no-redistribution limit for the phase curve and spectrum."
    },
    {
      "id": "co2-rich-retrieval",
      "label": "CO2-rich atmosphere retrieval family",
      "sample_size_or_observations": "Retrieval scenarios including a 0.3–1.0 bar pressure branch",
      "description": "Atmosphere-bearing retrieval models emphasizing CO2 greenhouse absorption (including the 15 um band) and exploring moderate-pressure solutions."
    },
    {
      "id": "o2-rich-retrieval",
      "label": "O2-rich atmosphere retrieval family",
      "sample_size_or_observations": "Retrieval scenarios with oxidized compositions and weaker CO2 constraints",
      "description": "Alternative atmosphere-bearing retrieval models with an O2-rich composition, which are discussed as not being fully excluded by current observations."
    }
  ],
  "major_limitations": [
    "Single-planet study; does not establish atmospheric prevalence across rocky M-dwarf planets.",
    "Quantitative retrieval posteriors depend on assumptions about circulation, opacity priors, and clouds.",
    "Stellar variability and instrument systematics may bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in the current dataset."
  ]
}

{
  "findings": [
