{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Can intratumoral delivery of an mRNA vaccine induce an interferon-rich tumor inflammatory state that increases sensitivity to anti-PD-L1 immune checkpoint blockade?",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy-combo",
      "label": "Mouse efficacy cohort (combination therapy)",
      "sample_size_or_observations": "78 tumor-bearing mice (pooled across repeat experiments)",
      "description": "Preclinical cohort of tumor-bearing mice treated with intratumoral mRNA vaccine plus anti-PD-L1 combination therapy, pooled across repeat experiments."
    },
    {
      "id": "mouse-controls",
      "label": "Mouse control cohort (vaccine only / ICI only / vehicle)",
      "sample_size_or_observations": "84 tumor-bearing mice (pooled across matched controls)",
      "description": "Matched preclinical control cohorts including vaccine-only, ICI-only, and vehicle control conditions, pooled across matched controls."
    },
    {
      "id": "human-retrospective-metastatic",
      "label": "Human retrospective metastatic cohort",
      "sample_size_or_observations": "130 patients total",
      "description": "Retrospective cohort of metastatic patients receiving ICI treatment, analyzed by prior SARS-CoV-2 mRNA vaccination status."
    },
    {
      "id": "human-vaccinated",
      "label": "Human vaccinated subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subgroup of the retrospective metastatic cohort with prior SARS-CoV-2 mRNA vaccination exposure."
    },
    {
      "id": "human-unvaccinated",
      "label": "Human unvaccinated subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Subgroup of the retrospective metastatic cohort without prior SARS-CoV-2 mRNA vaccination exposure."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized, so residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Tumor-type mix and treatment-history heterogeneity limit causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors.",
    "Some mechanistic readouts were strongest in specific model systems rather than uniformly across settings."
  ]
}

