{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Whether intratumoral delivery of an mRNA vaccine can induce an interferon-rich inflammatory state that increases tumour sensitivity to anti-PD-L1 immune checkpoint blockade.",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy-combo",
      "label": "Mouse efficacy cohort (combination therapy)",
      "sample_size_or_observations": "78 tumor-bearing mice pooled across repeat experiments",
      "description": "Preclinical murine tumour models testing intratumoral mRNA vaccination in combination with anti-PD-L1 as an efficacy setting for tumour control and immune infiltration readouts."
    },
    {
      "id": "mouse-matched-controls",
      "label": "Mouse control cohort (vaccine only, ICI only, vehicle)",
      "sample_size_or_observations": "84 tumor-bearing mice pooled across matched controls",
      "description": "Matched preclinical control groups used to distinguish combination effects from vaccine-only, ICI-only, or vehicle outcomes, and to support interpretation of sensitization versus standalone activity."
    },
    {
      "id": "human-retrospective-metastatic",
      "label": "Human retrospective metastatic cohort under ICI",
      "sample_size_or_observations": "130 patients total (43 vaccinated; 87 unvaccinated)",
      "description": "Translational retrospective cohort of metastatic patients receiving immune checkpoint inhibitor treatment, compared by prior SARS-CoV-2 mRNA vaccination status to assess associations with survival outcomes."
    }
  ],
  "major_limitations": [
    "Human analysis is retrospective and non-randomized; residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Tumor-type mix and treatment-history heterogeneity limit causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors.",
    "Some mechanistic readouts were strongest in specific model systems rather than uniformly across all tested settings."
  ]
}

