{
  "paper": {
    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does TRAPPIST-1 b retain an atmosphere despite strong stellar irradiation?",
  "datasets_or_cohorts": [
    {
      "id": "observed-system",
      "label": "Observed system",
      "sample_size_or_observations": "1 planet",
      "description": "Phase-resolved thermal spectra of the rocky exoplanet TRAPPIST-1 b."
    },
    {
      "id": "null-scenario",
      "label": "No-atmosphere model",
      "sample_size_or_observations": "model simulation",
      "description": "Bare-rock, no-atmosphere scenario with minimal heat redistribution."
    },
    {
      "id": "co2-rich",
      "label": "CO2-rich atmosphere scenario",
      "sample_size_or_observations": "model simulation",
