{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Can intratumoral mRNA vaccination sensitize tumors to immune checkpoint blockade and improve therapeutic outcomes?",
  "datasets_or_cohorts": [
    {
      "id": "c1",
      "label": "mouse efficacy",
      "sample_size_or_observations": "78 mice",
      "description": "Combination of intratumoral mRNA vaccine and anti‑PD‑L1 treatment across multiple murine tumor models (L19-L20)."
    },
    {
      "id": "c2",
      "label": "mouse control",
      "sample_size_or_observations": "84 mice",
      "description": "Matched control groups receiving vaccine only, ICI only, or vehicle (L19-L20)."
    },
    {
      "id": "c3",
      "label": "human retrospective cohort",
      "sample_size_or_observations": "130 patients",
      "description": "Metastatic cancer patients treated with ICIs, stratified by prior SARS‑CoV‑2 mRNA vaccination status (L21-L23)."
    },
    {
      "id": "c4",
      "label": "human vaccinated subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subset of the retrospective cohort who received an mRNA vaccine before ICI (L22-L23)."
    },
    {
      "id": "c5",
      "label": "human unvaccinated subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Subset of the retrospective cohort without prior mRNA vaccination (L22-L23)."
    }
  ],
  "major_limitations": [
    "Human analysis is retrospective and non‑randomized, introducing potential confounding (L48-L51).",
    "Vaccination timing relative to ICI initiation varied among patients (L50-L51).",
    "Mouse model findings may not translate directly to diverse human tumor types (L52-L53)."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Fraction of tumor proteins represented in immunopeptidome",
      "value": "62.3%",
