{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Can intratumoral mRNA vaccination sensitize tumors to immune checkpoint blockade therapy?",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy",
      "label": "Mouse efficacy cohort",
      "sample_size_or_observations": "78 mice",
      "description": "78 tumor‑bearing mice receiving combination of intratumoral mRNA vaccine and anti‑PD‑L1 therapy across repeat experiments."
    },
    {
      "id": "mouse-control",
      "label": "Mouse control cohort",
      "sample_size_or_observations": "84 mice",
      "description": "84 tumor‑bearing mice receiving vaccine‑only, ICI‑only, or vehicle control groups."
    },
    {
      "id": "human-retrospective",
      "label": "Human retrospective cohort",
      "sample_size_or_observations": "130 patients",
      "description": "130 metastatic cancer patients treated with immune checkpoint inhibitors, assessed by prior SARS‑CoV‑2 mRNA vaccination status."
    },
    {
      "id": "human-vaccinated",
      "label": "Vaccinated subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "43 patients in the retrospective cohort who had received a SARS‑CoV‑2 mRNA vaccine before ICI therapy."
    },
    {
      "id": "human-unvaccinated",
      "label": "Unvaccinated subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "87 patients in the retrospective cohort without prior mRNA vaccination."
    }
  ],
  "major_limitations": [
    "Retrospective human analysis is non‑randomized, risking confounding.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Mouse model findings may not translate directly to all human tumors."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Fraction of tumor proteins represented in immunopeptidome",
      "value": "62.3%",
      "comparator_or_baseline": "37.3%",
      "interpretation": "Broader antigen display after intratumoral mRNA vaccination",
      "evidence_line_refs": ["L28"]
    },
    {
      "id": "Q02",
      "metric": "Fraction of proteins represented in MHC-I peptidome",
      "value": "40.6%",
      "comparator_or_baseline": "20.6%",
      "interpretation": "Strong expansion of MHC-I‑presented landscape",
      "evidence_line_refs": ["L29"]
    },
    {
      "id": "Q03",
      "metric": "Retrospective human survival comparison p‑value",
      "value": "p=0.01",
      "comparator_or_baseline": "N/A",
      "interpretation": "Vaccinated subgroup had improved survival under ICI treatment",
      "evidence_line_refs": ["L30"]
    },
    {
      "id": "Q04",
      "metric": "Type I interferon response score (normalized units)",
      "value": "3.1",
      "comparator_or_baseline": "1.0",
      "interpretation": "Marked induction after mRNA vaccination in tumors",
      "evidence_line_refs": ["L31"]
    },
    {
      "id": "Q05",
      "metric": "PD‑L1 expression fold‑change in tumor tissue",
      "value": "2.4x",
      "comparator_or_baseline": "1.0x",
      "interpretation": "Checkpoint axis became more targetable",
      "evidence_line_refs": ["L32"]
    },
    {
      "id": "Q06",
      "metric": "Intratumoral CD8+ T‑cell density fold‑change",
      "value": "1.8x",
      "comparator_or_baseline": "1.0x",
      "interpretation": "Increased immune infiltration with combination therapy",
      "evidence_line_refs": ["L33"]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "Vaccine‑only treatment did not produce durable tumor control in non‑immunogenic models.",
      "evidence_line_ref": ["L36"]
    },
    {
      "statement": "Blocking type I interferon signaling with anti‑IFNAR1 eliminated the observed sensitization benefit.",
      "evidence_line_ref": ["L39"]
    }
  ]
}

{
  "claims": [
    {
      "claim_id": "C01",
      "claim": "Intratumoral mRNA vaccination combined with anti‑PD‑L1 therapy improves tumor control in mouse models.",
      "strength": "strong",
      "evidence": [
        {
          "quote": "Combined intervention improves tumor control in mice and aligns with better outcomes in the retrospective human cohort.",
          "line_ref": "L46"
        },
        {
          "quote": "Intratumoral mRNA vaccination triggers local innate sensing and type I interferon signaling.",
          "line_ref": "L42"
        }
      ],
      "caveat": "Mouse model results may not directly translate to human clinical outcomes."
    },
    {
      "claim_id": "C02",
      "claim": "mRNA vaccination markedly raises the tumor type I interferon response score.",
      "strength": "moderate",
      "evidence": [
        {
          "quote": "Type I interferon response score (normalized units) | 3.1 | 1.0",
          "line_ref": "L31"
        },
        {
          "quote": "Interferon-associated antigen processing and presentation pathways increase.",
          "line_ref": "L43"
        }
      ],
      "caveat": "The interferon score is a normalized metric and its clinical relevance is not fully established."
    },
    {
      "claim_id": "C03",
      "claim": "Patients previously vaccinated with SARS‑CoV‑2 mRNA vaccines show improved survival when treated with immune checkpoint inhibitors.",
      "strength": "moderate",
      "evidence": [
        {
          "quote": "Retrospective human survival comparison p‑value | p=0.01 | N/A",
          "line_ref": "L30"
        },
        {
          "quote": "Combined intervention improves tumor control in mice and aligns with better outcomes in the retrospective human cohort.",
          "line_ref": "L46"
        }
      ],
      "caveat": "The human analysis is retrospective and non‑randomized, so confounding factors may influence the observed survival benefit."
    },
    {
      "claim_id": "C04",
      "claim": "Blocking type I interferon signaling with anti‑IFNAR1 eliminates the sensitization effect of mRNA vaccination.",
      "strength": "strong",
      "evidence": [
        {
          "quote": "Blocking type I interferon signaling with anti‑IFNAR1 eliminated the observed sensitization benefit.",
          "line_ref": "L39"
        },
        {
          "quote": "Interferon‑associated antigen processing and presentation pathways increase.",
          "line_ref": "L43"
        }
      ],
      "caveat": "The blockade experiments were performed in preclinical models; human relevance requires further validation."
    }
  ]
}
