    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does TRAPPIST-1 b retain any atmosphere under strong stellar irradiation, as evidenced by JWST MIRI phase-curve spectroscopy measuring thermal phase variation and spectral structure around the 15 um CO2 band?",
  "datasets_or_cohorts": [
    {
      "id": "observed-system",
      "label": "Observed system",
      "sample_size_or_observations": "one rocky exoplanet",
      "description": "TRAPPIST-1 b phase-resolved thermal spectra obtained via JWST MIRI low-resolution spectroscopy over an orbital phase curve."
    },
    {
      "id": "null-scenario",
      "label": "Null scenario",
      "sample_size_or_observations": "bare-rock model",
      "description": "No-atmosphere model with minimal heat redistribution, serving as the baseline comparison for atmosphere detection."
    },
    {
      "id": "co2-rich-scenario",
      "label": "CO2-rich atmosphere scenario",
      "sample_size_or_observations": "atmosphere model",
      "description": "Atmosphere model with significant greenhouse gas absorption, testing for CO2 spectral features around 15 um."
    },
    {
      "id": "o2-rich-scenario",
      "label": "O2-rich atmosphere scenario",
      "sample_size_or_observations": "atmosphere model",
      "description": "Oxidized composition model with weaker CO2 signature constraints, representing an alternative atmospheric composition."
    }
  ],
  "major_limitations": [
    "This is a single-planet study and does not establish atmospheric prevalence across all M-dwarf rocky planets.",
    "Retrieval assumptions including circulation patterns, opacity priors, and cloud treatment affect quantitative posteriors.",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in the current dataset.",
    "Degeneracies between composition, clouds/hazes, and circulation assumptions remain significant."
  ]
}

