{
  "paper": {
    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does TRAPPIST-1 b retain any atmosphere under strong stellar irradiation, and can JWST phase-curve data provide evidence for or against an atmosphere-bearing scenario?",
  "datasets_or_cohorts": [
    {
      "id": "observed-system",
      "label": "TRAPPIST-1 b observed system",
      "sample_size_or_observations": "one rocky exoplanet with phase-resolved thermal spectra",
      "description": "The observed target is a single Earth-sized rocky exoplanet on a short, tidally locked orbit around an M dwarf, observed with JWST MIRI phase-resolved spectroscopy."
    },
    {
      "id": "null-scenario",
      "label": "Bare-rock null scenario",
      "sample_size_or_observations": "atmosphere-free model",
      "description": "A theoretical comparison case representing a bare-rock planet with no atmosphere and minimal heat redistribution, serving as the baseline null hypothesis."
    },
    {
      "id": "co2-rich-scenario",
      "label": "CO2-rich atmosphere scenario",
      "sample_size_or_observations": "atmosphere-bearing model",
      "description": "A theoretical comparison case representing an atmosphere with significant greenhouse gas absorption, particularly CO2, with preferred pressure scale 0.3-1.0 bar."
    },
    {
      "id": "o2-rich-scenario",
      "label": "O2-rich atmosphere scenario",
      "sample_size_or_observations": "alternative atmosphere model",
      "description": "A theoretical comparison case representing an oxidized atmospheric composition with weaker CO2 signature constraints, not fully ruled out by current data."
    }
  ],
  "major_limitations": [
    "Single-planet study that does not establish atmospheric prevalence across all M-dwarf rocky planets.",
    "Retrieval assumptions including circulation patterns, opacity priors, and cloud treatment affect quantitative posteriors.",
    "Stellar variability and instrument systematics can bias weak spectral features.",
    "Night-side constraints are weaker than day-side constraints in the current dataset.",
    "Degeneracies between composition, clouds/hazes, and circulation assumptions remain significant."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Dayside brightness temperature",
      "value": "342 +/- 22 K",
      "comparator_or_baseline": "Bare-rock expectation from no-redistribution model",
      "interpretation": "Day side is warm but not sufficient alone to prove a bare surface.",
      "evidence_line_refs": ["L27"]
    },
    {
      "id": "Q02",
      "metric": "Heat transport efficiency (epsilon)",
      "value": "0.19 (+0.23 / -0.14)",
      "comparator_or_baseline": "0.0 for perfect no-atmosphere limit",
      "interpretation": "Non-zero redistribution is favored, implying transport beyond immediate bare-rock reradiation limit.",
      "evidence_line_refs": ["L28"]
    },
    {
      "id": "Q03",
      "metric": "Bond albedo upper bound",
      "value": "<0.08",
      "comparator_or_baseline": "Higher-albedo reflective cases",
      "interpretation": "Planet appears very dark in thermal-energy balance fits.",
      "evidence_line_refs": ["L29"]
    },
    {
      "id": "Q04",
      "metric": "15 um CO2 spectral band depth estimate",
      "value": "96 ppm",
      "comparator_or_baseline": "Flat-spectrum null",
      "interpretation": "Spectral structure is consistent with atmospheric absorption.",
      "evidence_line_refs": ["L30"]
    },
    {
      "id": "Q05",
      "metric": "Joint fit likelihood ratio (atmosphere vs no atmosphere)",
      "value": "7.4",
      "comparator_or_baseline": "1.0 baseline ratio",
      "interpretation": "Atmosphere-bearing models better explain combined data products.",
      "evidence_line_refs": ["L31"]
    },
    {
      "id": "Q06",
      "metric": "Preferred pressure scale in CO2-rich retrieval family",
      "value": "0.3-1.0 bar",
      "comparator_or_baseline": "Near-vacuum branch",
      "interpretation": "Moderate-pressure atmosphere family remains plausible.",
      "evidence_line_refs": ["L32"]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "A pure no-atmosphere model cannot simultaneously match both phase amplitude and spectral band behavior.",
      "evidence_line_ref": "L35"
    },
    {
      "statement": "O2-rich atmosphere cases are not fully ruled out by current data.",
      "evidence_line_ref": "L36"
    },
    {
      "statement": "Degeneracies between composition, clouds/hazes, and circulation assumptions remain significant.",
      "evidence_line_ref": "L37"
    }
  ]
}

{
  "claims": [
    {
      "claim_id": "C01",
      "claim": "TRAPPIST-1 b exhibits measurable heat redistribution between its day and night sides, inconsistent with a perfectly airless bare-rock scenario.",
      "strength": "moderate",
      "evidence": [
        {
          "quote": "Heat transport efficiency (epsilon) | 0.19 (+0.23 / -0.14) | 0.0 for perfect no-atmosphere limit | Non-zero redistribution is favored",
          "line_ref": "L28"
        },
        {
          "quote": "A non-zero redistribution parameter implies transport beyond an immediate bare-rock reradiation limit.",
          "line_ref": "L41"
        }
      ],
      "caveat": "The heat transport efficiency has large uncertainties (+0.23 / -0.14) and overlaps with the zero-redistribution case, so the evidence is suggestive but not definitive."
    },
    {
      "claim_id": "C02",
      "claim": "The 15 um CO2 spectral band shows detectable structure consistent with atmospheric absorption rather than a flat featureless spectrum.",
      "strength": "moderate",
      "evidence": [
        {
