{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Can intratumoral delivery of mRNA vaccines create an interferon-rich inflammatory state that improves tumor sensitivity to anti-PD-L1 immune checkpoint inhibitor therapy?",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy",
      "label": "Mouse efficacy cohort (combination therapy)",
      "sample_size_or_observations": "78 tumor-bearing mice",
      "description": "Pooled across repeat experiments testing intratumoral mRNA vaccine combined with anti-PD-L1 checkpoint blockade in multiple murine tumor models."
    },
    {
      "id": "mouse-control",
      "label": "Mouse control cohort",
      "sample_size_or_observations": "84 tumor-bearing mice",
      "description": "Pooled across matched controls including vaccine-only, ICI-only, and vehicle-treated groups in murine tumor models."
    },
    {
      "id": "human-retrospective",
      "label": "Human retrospective metastatic cohort",
      "sample_size_or_observations": "130 patients total",
      "description": "Retrospective cohort of metastatic patients receiving ICI treatment, analyzed by prior SARS-CoV-2 mRNA vaccination status."
    },
    {
      "id": "human-vaccinated",
      "label": "Human vaccinated subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subset of the retrospective metastatic cohort who had received prior SARS-CoV-2 mRNA vaccination before ICI treatment."
    },
    {
      "id": "human-unvaccinated",
      "label": "Human unvaccinated subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Subset of the retrospective metastatic cohort who had not received SARS-CoV-2 mRNA vaccination prior to ICI treatment."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized; residual confounding is likely.",
    "Vaccination timing relative to ICI initiation varied across patients.",
    "Tumor-type mix and treatment-history heterogeneity limit direct causal interpretation.",
    "Mouse model effects may not map one-to-one to all human tumors.",
    "Some mechanistic readouts were strongest in specific model systems rather than uniformly across all tested settings."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Fraction of tumor proteins represented in immunopeptidome",
      "value": "62.3%",
      "comparator_or_baseline": "37.3%",
      "interpretation": "Broader antigen display after intratumoral mRNA vaccination",
      "evidence_line_refs": ["L28"]
    },
    {
      "id": "Q02",
      "metric": "Fraction of proteins represented in MHC-I peptidome",
      "value": "40.6%",
      "comparator_or_baseline": "20.6%",
      "interpretation": "Strong expansion of MHC-I-presented landscape",
      "evidence_line_refs": ["L29"]
    },
    {
      "id": "Q03",
      "metric": "Retrospective human survival comparison p-value",
      "value": "p=0.01",
      "comparator_or_baseline": "N/A",
      "interpretation": "Vaccinated subgroup had improved survival under ICI treatment",
      "evidence_line_refs": ["L30"]
    },
    {
      "id": "Q04",
      "metric": "Type I interferon response score (normalized units)",
      "value": "3.1",
      "comparator_or_baseline": "1.0",
      "interpretation": "Marked induction after mRNA vaccination in tumors",
      "evidence_line_refs": ["L31"]
    },
    {
      "id": "Q05",
      "metric": "PD-L1 expression fold-change in tumor tissue",
      "value": "2.4x",
      "comparator_or_baseline": "1.0x",
      "interpretation": "Checkpoint axis became more targetable",
      "evidence_line_refs": ["L32"]
    },
    {
      "id": "Q06",
      "metric": "Intratumoral CD8+ T-cell density fold-change",
      "value": "1.8x",
      "comparator_or_baseline": "1.0x",
      "interpretation": "Increased immune infiltration with combination therapy",
      "evidence_line_refs": ["L33"]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "Vaccine-only treatment did not produce durable tumor control in non-immunogenic models.",
      "evidence_line_ref": "L36"
    },
    {
      "statement": "ICI-only treatment had limited activity in low-immunogenic baseline settings.",
      "evidence_line_ref": "L37"
    },
    {
      "statement": "Lipid particle controls lacking mRNA did not reproduce the sensitization effect.",
      "evidence_line_ref": "L38"
    },
    {
      "statement": "Blocking type I interferon signaling with anti-IFNAR1 eliminated the observed sensitization benefit.",
      "evidence_line_ref": "L39"
    }
  ]
}

