    },
    {
      "id": "null-scenario",
      "label": "Bare-rock Null Scenario",
      "sample_size_or_observations": "Model comparison scenario",
      "description": "A pure no-atmosphere model with minimal heat redistribution, serving as the null hypothesis to test whether an atmosphere is required to explain the observations."
    },
    {
      "id": "co2-rich-scenario",
      "label": "CO2-rich Atmosphere Scenario",
      "sample_size_or_observations": "Model comparison scenario",
      "description": "An atmosphere model with significant greenhouse gas absorption, particularly targeting the CO2 spectral feature near 15 um to assess whether carbon dioxide can explain the observed spectral structure."
    },
    {
      "id": "o2-rich-scenario",
      "label": "O2-rich Atmosphere Scenario",
      "sample_size_or_observations": "Model comparison scenario",
      "description": "An oxidized atmospheric composition with weaker CO2 signature constraints, representing an alternative composition that could also potentially match the data."
    }
  ],
  "major_limitations": [
    "This is a single-planet study and does not establish atmospheric prevalence across all M-dwarf rocky planets.",
    "Retrieval assumptions including circulation models, opacity priors, and cloud treatment affect quantitative posteriors and could bias results.",
    "Stellar variability and instrument systematics can bias weak spectral features, introducing uncertainty into the detection claims.",
    "Night-side constraints are weaker than day-side constraints in the current dataset due to observational geometry.",
    "Degeneracies between atmospheric composition, cloud/haze properties, and circulation assumptions remain significant and limit compositional constraints."
  ]
}

