    {
      "id": "co2-rich-atmosphere",
      "label": "CO2-rich atmosphere scenario",
      "sample_size_or_observations": "Model retrieval scenario",
      "description": "Atmosphere model incorporating significant greenhouse gas absorption, particularly targeting the 15 um spectral band where CO2 creates a characteristic spectral signature."
    },
    {
      "id": "o2-rich-atmosphere",
      "label": "O2-rich atmosphere scenario",
      "sample_size_or_observations": "Model retrieval scenario",
      "description": "Oxidized atmospheric composition scenario with weaker CO2 signature constraints, representing a potential alternative atmospheric chemistry for tidally locked rocky planets."
    }
  ],
  "major_limitations": [
    "This is a single-planet study and does not establish atmospheric prevalence across all M-dwarf rocky planets.",
    "Retrieval assumptions including circulation models, opacity priors, and cloud treatment affect quantitative posteriors and conclusions.",
    "Stellar variability and instrument systematics can bias weak spectral features and introduce uncertainties in atmospheric detections.",
    "Night-side constraints are weaker than day-side constraints in the current JWST MIRI dataset, limiting complete atmospheric characterization."
  ]
}

