    },
    {
      "id": "bare-rock-model",
      "label": "No-atmosphere bare-rock scenario",
      "sample_size_or_observations": "Model comparison baseline",
      "description": "Theoretical model representing an airless rocky planet with minimal heat redistribution between day and night sides."
    },
    {
      "id": "co2-rich-atmosphere",
      "label": "CO2-rich atmosphere retrieval family",
