{
  "paper": {
    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does TRAPPIST-1 b retain an atmosphere despite strong stellar irradiation from its M dwarf host star, and what are the constraints on its composition?",
  "datasets_or_cohorts": [
    {
      "id": "trappist-1-b",
      "label": "TRAPPIST-1 b Observation",
      "sample_size_or_observations": "JWST MIRI low-resolution spectroscopy across full orbital phase curve",
      "description": "Earth-sized rocky exoplanet on tidally locked orbit around M dwarf star, observed with JWST MIRI to capture thermal phase variations and spectral structure."
    },
    {
      "id": "no-atmosphere-scenario",
      "label": "Bare-rock No-atmosphere Model",
      "sample_size_or_observations": "Null hypothesis model for comparison",
      "description": "Theoretical model assuming a bare rocky surface with no atmospheric layer, minimal heat redistribution, and immediate reradiation of stellar flux."
    },
    {
      "id": "co2-rich-atmosphere",
      "label": "CO2-rich Atmosphere Scenario",
      "sample_size_or_observations": "Retrieval model with greenhouse gas absorption",
      "description": "Atmosphere model incorporating significant carbon dioxide absorption, testing whether the 15 um spectral band indicates presence of CO2-rich atmosphere."
    },
    {
      "id": "o2-rich-atmosphere",
      "label": "O2-rich Atmosphere Scenario",
      "sample_size_or_observations": "Alternative composition retrieval",
      "description": "Atmospheric model assuming oxidized composition with weaker CO2 signature constraints, representing an alternative plausible atmospheric state."
    }
  ],
  "major_limitations": [
    "Single-planet study does not establish atmospheric prevalence across all M-dwarf rocky planets",
    "Retrieval assumptions regarding circulation, opacity priors, and cloud treatment affect quantitative posteriors",
    "Stellar variability and instrument systematics may bias weak spectral feature detection",
    "Night-side observational constraints are weaker than day-side constraints in current dataset",
    "Degeneracies remain between atmospheric composition, cloud/haze properties, and circulation assumptions"
  ]
}

