{
  "paper": {
    "title": "Phase-curve Evidence for an Atmosphere on TRAPPIST-1 b",
    "doi": "10.48550/arXiv.2409.13036",
    "publication_year": 2024,
    "domain": "exoplanet-atmospheres",
    "study_type": "observational-astronomy"
  },
  "core_question": "Does TRAPPIST-1 b retain an atmosphere under strong stellar irradiation from its M dwarf host, and what constraints do JWST MIRI phase-curve observations provide on atmospheric composition and heat redistribution?",
  "datasets_or_cohorts": [
    {
      "id": "trappist-1b-observations",
      "label": "TRAPPIST-1 b Phase-Curve Observations",
      "sample_size_or_observations": "JWST MIRI low-resolution spectroscopy over complete orbital phase curve",
      "description": "Thermal phase variation and spectral structure observations around the 15 um CO2 band for TRAPPIST-1 b using JWST MIRI instrument"
    },
    {
      "id": "bare-rock-model",
      "label": "Bare-Rock No-Atmosphere Model",
      "sample_size_or_observations": "Null scenario model",
      "description": "Theoretical model assuming no atmosphere with minimal heat redistribution, representing a bare rocky surface that immediately reradiates absorbed stellar energy"
    },
    {
      "id": "co2-rich-atmosphere",
      "label": "CO2-Rich Atmosphere Scenario",
      "sample_size_or_observations": "Atmosphere retrieval model",
      "description": "Atmospheric model incorporating significant greenhouse gas absorption from CO2, testing whether observed spectral features match predicted signatures"
    },
    {
      "id": "o2-rich-atmosphere",
      "label": "O2-Rich Atmosphere Scenario",
      "sample_size_or_observations": "Alternative composition model",
      "description": "Atmospheric model assuming oxidized composition with different spectral properties, providing alternative composition constraints"
    }
  ],
  "major_limitations": [
    "This is a single-planet study and does not establish atmospheric prevalence across all M-dwarf rocky planets",
    "Retrieval assumptions including circulation patterns, opacity priors, and cloud treatment affect quantitative posteriors",
    "Stellar variability and instrument systematics can bias interpretation of weak spectral features",
    "Night-side constraints are weaker than day-side constraints in the current dataset due to observation geometry"
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Dayside brightness temperature",
      "value": "342 +/- 22 K",
      "comparator_or_baseline": "Bare-rock expectation from no-redistribution model",
      "interpretation": "The day side is warm but this temperature alone is not sufficient to prove a bare surface; intermediate temperatures are consistent with both atmosphere-bearing and bare-rock scenarios",
      "evidence_line_refs": ["L27"]
    },
    {
      "id": "Q02",
      "metric": "Heat transport efficiency (epsilon)",
      "value": "0.19 (+0.23 / -0.14)",
      "comparator_or_baseline": "0.0 for perfect no-atmosphere limit",
      "interpretation": "Non-zero redistribution is favored, indicating that energy is being transported from day to night side beyond what would occur on an airless body",
      "evidence_line_refs": ["L28"]
    },
    {
      "id": "Q03",
      "metric": "Bond albedo upper bound",
      "value": "<0.08",
      "comparator_or_baseline": "Higher-albedo reflective cases",
      "interpretation": "Planet appears very dark in thermal-energy balance fits, which constrains surface and atmospheric reflective properties",
      "evidence_line_refs": ["L29"]
    },
    {
      "id": "Q04",
      "metric": "15 um CO2 spectral band depth estimate",
      "value": "96 ppm",
      "comparator_or_baseline": "Flat-spectrum null",
      "interpretation": "Spectral structure is consistent with atmospheric absorption signatures expected from CO2-rich atmospheric compositions",
      "evidence_line_refs": ["L30"]
    },
    {
      "id": "Q05",
      "metric": "Joint fit likelihood ratio",
      "value": "7.4",
      "comparator_or_baseline": "1.0 baseline ratio",
      "interpretation": "Atmosphere-bearing models better explain combined data products, providing statistical support for atmospheric presence",
      "evidence_line_refs": ["L31"]
    },
    {
      "id": "Q06",
      "metric": "Preferred pressure scale in CO2-rich retrieval",
      "value": "0.3-1.0 bar",
      "comparator_or_baseline": "Near-vacuum branch",
      "interpretation": "Moderate-pressure atmosphere family remains plausible given current data, though pressure constraints are not definitive",
      "evidence_line_refs": ["L32"]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "A pure no-atmosphere model cannot simultaneously match both phase amplitude and spectral band behavior",
      "evidence_line_ref": "L35"
    },
    {
      "statement": "O2-rich atmosphere cases are not fully ruled out by current data",
      "evidence_line_ref": "L36"
    },
    {
      "statement": "Degeneracies between composition, clouds/hazes, and circulation assumptions remain significant",
      "evidence_line_ref": "L37"
    },
    {
      "statement": "Joint constraints from phase curve and spectrum are stronger than either observable alone",
      "evidence_line_ref": "L43"
    },
    {
      "statement": "Remaining degeneracies motivate broader wavelength coverage and repeated observations",
      "evidence_line_ref": "L44"
    }
  ]
}

