{
  "paper": {
    "title": "SARS-CoV-2 mRNA vaccines sensitize tumours to immune checkpoint blockade",
    "doi": "10.1038/s41586-025-09006-8",
    "publication_year": 2025,
    "domain": "oncology-immunology",
    "study_type": "preclinical+retrospective"
  },
  "core_question": "Can intratumoral mRNA vaccination create an interferon-rich inflammatory state that improves sensitivity to anti-PD-L1 therapy in tumors?",
  "datasets_or_cohorts": [
    {
      "id": "mouse-efficacy",
      "label": "Mouse efficacy cohort (combination therapy)",
      "sample_size_or_observations": "78 tumor-bearing mice pooled across repeat experiments",
      "description": "Murine tumor models receiving intratumoral mRNA vaccine combined with anti-PD-L1 therapy to assess tumor control efficacy."
    },
    {
      "id": "mouse-control",
      "label": "Mouse control cohort",
      "sample_size_or_observations": "84 tumor-bearing mice pooled across matched controls",
      "description": "Matched control groups including vaccine-only treatment, ICI-only treatment, and vehicle controls to establish comparative baselines."
    },
    {
      "id": "human-metastatic",
      "label": "Human retrospective metastatic cohort",
      "sample_size_or_observations": "130 patients total",
      "description": "Retrospective cohort of metastatic patients receiving ICI treatment to assess real-world outcomes stratified by vaccination status."
    },
    {
      "id": "human-vaccinated",
      "label": "Human vaccinated subgroup",
      "sample_size_or_observations": "43 patients",
      "description": "Subset of metastatic ICI-treated patients with prior SARS-CoV-2 mRNA vaccination history for comparative analysis."
    },
    {
      "id": "human-unvaccinated",
      "label": "Human unvaccinated subgroup",
      "sample_size_or_observations": "87 patients",
      "description": "Subset of metastatic ICI-treated patients without prior SARS-CoV-2 mRNA vaccination for baseline comparison."
    }
  ],
  "major_limitations": [
    "The human analysis is retrospective and non-randomized, introducing potential residual confounding that could affect outcome interpretation.",
    "Vaccination timing relative to ICI initiation varied across patients, creating heterogeneity in treatment sequencing that may influence results.",
    "Tumor-type mix and treatment-history heterogeneity across the human cohort limit direct causal interpretation of the vaccination effect.",
    "Mouse model effects may not map one-to-one to all human tumors due to species differences in tumor biology and immune responses.",
    "Some mechanistic readouts were strongest in specific model systems rather than uniformly across all tested settings, suggesting context-dependent effects."
  ]
}

{
  "findings": [
    {
      "id": "Q01",
      "metric": "Fraction of tumor proteins represented in immunopeptidome",
      "value": "62.3%",
      "comparator_or_baseline": "37.3% (comparator)",
      "interpretation": "Intratumoral mRNA vaccination substantially broadened the antigen display landscape, nearly doubling the proportion of tumor proteins presented in the immunopeptidome.",
      "evidence_line_refs": ["L28"]
    },
    {
      "id": "Q02",
      "metric": "Fraction of proteins represented in MHC-I peptidome",
      "value": "40.6%",
      "comparator_or_baseline": "20.6% (comparator)",
      "interpretation": "MHC-I antigen presentation capacity nearly doubled following vaccination, suggesting enhanced tumor visibility to cytotoxic T-cell surveillance.",
      "evidence_line_refs": ["L29"]
    },
    {
      "id": "Q03",
      "metric": "Retrospective human survival comparison p-value",
      "value": "p=0.01",
      "comparator_or_baseline": "N/A",
      "interpretation": "The vaccinated subgroup demonstrated statistically significant improved survival under ICI treatment compared to unvaccinated patients.",
      "evidence_line_refs": ["L30"]
    },
    {
      "id": "Q04",
      "metric": "Type I interferon response score (normalized units)",
      "value": "3.1",
      "comparator_or_baseline": "1.0 (comparator)",
      "interpretation": "Marked induction of type I interferon signaling was observed in tumors following mRNA vaccination, indicating robust innate immune activation.",
      "evidence_line_refs": ["L31"]
    },
    {
      "id": "Q05",
      "metric": "PD-L1 expression fold-change in tumor tissue",
      "value": "2.4x",
      "comparator_or_baseline": "1.0x (comparator)",
      "interpretation": "PD-L1 expression increased substantially in tumor tissue following treatment, rendering the checkpoint axis more targetable for ICI therapy.",
      "evidence_line_refs": ["L32"]
    },
    {
      "id": "Q06",
      "metric": "Intratumoral CD8+ T-cell density fold-change",
      "value": "1.8x",
      "comparator_or_baseline": "1.0x (comparator)",
      "interpretation": "Combination therapy resulted in nearly doubled CD8+ T-cell infiltration into tumors, indicating enhanced immune cell recruitment and tumor infiltration.",
      "evidence_line_refs": ["L33"]
    }
  ],
  "controls_or_null_results": [
    {
      "statement": "Vaccine-only treatment did not produce durable tumor control in non-immunogenic models, demonstrating the necessity of combining mRNA vaccination with checkpoint blockade.",
      "evidence_line_ref": "L36"
    },
    {
      "statement": "ICI-only treatment had limited activity in low-immunogenic baseline settings, highlighting the importance of prior immune priming through vaccination.",
      "evidence_line_ref": "L37"
    },
    {
      "statement": "Lipid particle controls lacking mRNA did not reproduce the sensitization effect, confirming that the mRNA component is essential for the observed biological activity.",
      "evidence_line_ref": "L38"
    },
    {
      "statement": "Blocking type I interferon signaling with anti-IFNAR1 eliminated the observed sensitization benefit, demonstrating that interferon signaling is mechanistically required for the vaccine's effects.",
      "evidence_line_ref": "L39"
    }
  ]
}

